import styled from "styled-components";
import { useTranslation } from "react-i18next";
import { useFormik, FormikHelpers } from "formik";
import * as Yup from "yup";
import { useState } from "react";
import { useDispatch } from "react-redux";

import { StyledSignUp, ComponentFromEmail } from "@components";
import { DAYS, MONTHS, YEARS } from "@configs";
import { IDate, IEmailForm, ISelect } from "@interfaces";
import { useYup } from "@validations";
import { setLoading } from "@redux";
import { authApi } from "@api";
import { useNotify } from "@utils";

export const ModuleEmail = () => {
    //page hook
    const { t } = useTranslation();
    const { YubEmailSignUpForm } = useYup();
    const dispatch = useDispatch();
    const { error, success } = useNotify();

    //page state
    const [date, setDate] = useState<IDate>({
        day: "",
        month: "",
        year: "",
        error: "",
    });
    //page variable
    const daySelects: ISelect[] = DAYS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });
    const monthSelects: ISelect[] = MONTHS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });
    const yearSelects: ISelect[] = YEARS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });

    const initialValues: IEmailForm = {
        password: "",
        conformPassword: "",
        fullName: "",
        tel: "",
        dateOrBirth: "",
        acceptTermsAndConditions: false,
    };

    const registerSchema = Yup.object().shape(YubEmailSignUpForm);

    const handleChangeDay = (value: string) => {
        setDate({
            ...date,
            day: value,
            error: "",
        });
    };
    const handleChangeMonth = (value: string) => {
        setDate({
            ...date,
            month: value,
            error: "",
        });
    };
    const handleChangeYear = (value: string) => {
        setDate({
            ...date,
            year: value,
            error: "",
        });
    };

    const handleChangeAdditionField = (value: string) => {
        //
        value;
    };

    const handleRegister = async (
        values: IEmailForm,
        { setSubmitting }: FormikHelpers<IEmailForm>
    ) => {
        if (!date.day || !date.month || !date.year) {
            setDate({
                ...date,
                error: t("validation.is_required"),
            });
            return;
        }

        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const dateForm = new Date(date.day + "/" + date.month + "/" + date.year);
            const res = await authApi.createPassword({ ...values, dateOrBirth: dateForm });
            if (res) {
                success(t("message.sign_up.success"));
            }
        } catch (errors: any) {
            //
            const errorsResponse = errors.response.errors;
            if (Array.isArray(errorsResponse) && errorsResponse.length) {
                error(errorsResponse[0]);
            } else {
                error(t("message.sign_up.fail"));
            }
        } finally {
            //
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };

    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleRegister,
        });

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.auth.fill_in_the_information_and_receive_special_benefits")}{" "}
                </div>
            </StyledContainer>
            <ComponentFromEmail
                handleBlur={handleBlur}
                handleChange={handleChange}
                errors={errors}
                touched={touched}
                values={values}
                isSubmitting={isSubmitting}
                days={daySelects}
                months={monthSelects}
                years={yearSelects}
                onChangeDay={handleChangeDay}
                onChangeMonth={handleChangeMonth}
                onChangeYear={handleChangeYear}
                onChangeAdditionField={handleChangeAdditionField}
                handleSubmit={handleSubmit}
                date={date}
            />
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        max-width: 70%;
        margin: 0 auto 46px auto;
    }
`;
