import { useTranslation } from "react-i18next";
import styled from "styled-components";
import OtpInput from "react-otp-input";

import { theme, useWindowDimensions } from "@utils";
import { SharedButton } from "@components";

export const ComponentUserVerify = () => {
    //page hooks
    const { t } = useTranslation();
    const { width } = useWindowDimensions();

    //page variable
    const margin = width <= 380 ? (width <= 340 ? "0.1rem" : "0.3rem") : "0.5rem";
    const checkWidth = width < 380 ? (width < 350 ? "2.3rem" : "2.5rem") : "3rem";

    return (
        <StyledUserVerify>
            <div className="title">{t("page.verify.phone_desc")}</div>
            <div className="enter_it">{t("page.verify.enter_it_here")}</div>
            <OtpInput
                numInputs={6}
                inputStyle={{
                    width: checkWidth,
                    height: "3rem",
                    margin: `1rem ${margin}`,
                    fontSize: "2rem",
                    borderRadius: "4px",
                    border: "none",
                    background: "#fff",
                }}
                containerStyle={{
                    justifyContent: "center",
                    flexWrap: "wrap",
                    margin: "1rem 0",
                }}
                isInputNum
            />
            <StyledSharedButton size="large" bg_color={theme.colors.green} type="submit">
                {t("page.auth.save")}
            </StyledSharedButton>
        </StyledUserVerify>
    );
};

const StyledUserVerify = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: calc(100vh - 100px);
    width: 100%;
    flex-direction: column;
    .title {
        font-family: "Leelawadee UI", "Roboto", sans-serif;
        font-style: normal;
        font-size: 18px;
        font-weight: 600;
        line-height: 24px;
    }
    .enter_it {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-weight: 600;
        font-size: 14px;
        line-height: 19px;
    }
    .allow-dropdown.intl-tel-input {
        width: 100% !important;
        background-color: #fff;
    }
    .intl-tel-input.allow-dropdown input[type="text"],
    .intl-tel-input.allow-dropdown input[type="tel"] {
        height: 48px !important;
        outline: none;
        border: none;
        background-color: #fff;
        width: 100% !important;
        font-size: 16px;
        color: black;
        box-shadow: none !important;
        font-weight: 500;
    }
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
    position: absolute;
    bottom: 48px;
    left: 5%;
`;
