import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { selectMerchant } from "@redux";

export const ComponentMembershipTier = () => {
    //hooks
    const { t } = useTranslation();
    //redux page
    const { membershipTiers } = useSelector(selectMerchant);
    console.log(membershipTiers);

    return (
        <StyledMemberShipTier>
            <div className="title">{t("page.memberShipTier.membership_tier")}</div>
            <div className="desc">{t("page.memberShipTier.desc")}</div>
            {membershipTiers?.map((membershipTier) => {
                return (
                    <div className="membershipTiers">
                        <div className="membershipTiers__info">
                            <div className="image">
                                <img src={membershipTier.iconUrl} />
                            </div>
                            <div className="rank">
                                <div className="name">{membershipTier.membershipName}</div>
                                <div className="membershipTiers_desc">
                                    {t("page.memberShipTier.earn_points_in_a_year", {
                                        point: membershipTier.points,
                                    })}
                                </div>
                            </div>
                        </div>
                    </div>
                );
            })}
        </StyledMemberShipTier>
    );
};

const StyledMemberShipTier = styled.div`
    width: 88%;
    margin: 0 auto;
    padding: 0 24px 32px 24px;
    background-color: #fff;
    box-shadow: rgb(204 204 204) 0px 0px 3px 2px;
    border-radius: 12px;
    .title {
        font-family: Segoe UI;
        font-style: normal;
        font-weight: 600;
        font-size: 20px;
        color: #000000;
        padding: 28px 0 18px 0;
    }
    .desc {
        font-family: Segoe UI;
        font-style: normal;
        font-weight: normal;
        font-size: 16px;
        color: #000000;
        margin-bottom: 30px;
    }
    .membershipTiers {
        display: flex;
        .membershipTiers__info {
            display: flex;
            align-items: center;
        }
        .rank {
            margin-left: 16px;
        }
    }
`;
