import { FormikHelpers, useFormik } from "formik";
import { useTranslation } from "react-i18next";
import * as Yup from "yup";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { StyledSignUp, ComponentFromSocial, StyledContainerSocial } from "@components";
import { DAYS, enumExternalMethod, MONTHS, PATH_HOME, YEARS } from "@configs";
import { IDate, ISelect, ISocialMedia, ISurveyQuestions } from "@interfaces";
import { useYup } from "@validations";
import { selectAuth, selectMerchant, setExternalLogin, setLoading } from "@redux";
import { authApi } from "@api";
import { useHistory } from "react-router";
import { useNotify } from "@utils";

export const ModuleSocial = () => {
    //page hook
    const { t } = useTranslation();
    const { YubSocialSignUp } = useYup();
    const dispatch = useDispatch();
    const history = useHistory();
    const { error, success } = useNotify();
    //page redux
    const { externalLogin } = useSelector(selectAuth);
    const merchant = useSelector(selectMerchant);
    //page state
    const [date, setDate] = useState<IDate>({
        day: "",
        month: "",
        year: "",
        error: "",
    });
    const [surveyQuestions, setSurveyQuestions] = useState<ISurveyQuestions[]>([]);

    //page variable
    const daySelects: ISelect[] = DAYS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });
    const monthSelects: ISelect[] = MONTHS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });
    const yearSelects: ISelect[] = YEARS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });

    const initialValues: ISocialMedia = {
        fullName: "",
        tel: "",
        dateOrBirth: "",
        acceptTermsAndConditions: false,
        email: "",
    };
    const registerSchema = Yup.object().shape(YubSocialSignUp);
    const handleChangeDay = (value: string) => {
        setDate({
            ...date,
            day: value,
            error: "",
        });
    };
    const handleChangeMonth = (value: string) => {
        setDate({
            ...date,
            month: value,
            error: "",
        });
    };
    const handleChangeYear = (value: string) => {
        setDate({
            ...date,
            year: value,
            error: "",
        });
    };

    const handleChangeAdditionField = (value: string) => {
        //
        value;
    };

    const handleRegister = async (
        values: ISocialMedia,
        { setSubmitting }: FormikHelpers<ISocialMedia>
    ) => {
        if (!date.day || !date.month || !date.year) {
            setDate({
                ...date,
                error: t("validation.is_required"),
            });
            return;
        }
        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const dateForm = new Date(date.day + "/" + date.month + "/" + date.year);
            const res = await authApi.createPassword({
                ...values,
                dateOrBirth: dateForm,
            });
            if (res) {
                success(t("message.sign_up.success"));
                history.push(PATH_HOME);
                dispatch(
                    setExternalLogin({
                        type: externalLogin || enumExternalMethod.GOOGLE,
                        isSignUp: false,
                    })
                );
            }
        } catch (errors: any) {
            const errorsResponse = errors.response.data.errors;
            if (Array.isArray(errorsResponse) && errorsResponse.length) {
                error(errorsResponse[0]);
            } else {
                error(t("message.sign_up.fail"));
            }
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
        //
    };

    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleRegister,
        });

    const handleChangeAnswerByIndex = (answer: string | string[], index: number) => {
        if (Array.isArray(answer)) {
            surveyQuestions[index].answers = answer;
        } else {
            surveyQuestions[index].answers = [answer];
        }
        setSurveyQuestions([...surveyQuestions]);
    };

    return (
        <StyledSignUp>
            <StyledContainerSocial>
                <div className="title">
                    {t("page.auth.fill_in_the_information_and_receive_special_benefits")}{" "}
                </div>
            </StyledContainerSocial>
            <ComponentFromSocial
                handleBlur={handleBlur}
                handleChange={handleChange}
                errors={errors}
                touched={touched}
                values={values}
                isSubmitting={isSubmitting}
                days={daySelects}
                months={monthSelects}
                years={yearSelects}
                onChangeDay={handleChangeDay}
                onChangeMonth={handleChangeMonth}
                onChangeYear={handleChangeYear}
                onChangeAdditionField={handleChangeAdditionField}
                handleSubmit={handleSubmit}
                date={date}
            />
        </StyledSignUp>
    );
};
