import { useFormik, FormikHelpers } from "formik";
import * as Yup from "yup";
import { useDispatch } from "react-redux";
import { useState } from "react";
import { CountryData } from "react-intl-tel-input";
import { useTranslation } from "react-i18next";

import { ComponentSignUpTel } from "@components";
import { IPhone } from "@interfaces";
import { useYup } from "@validations";
import { authApi } from "@api";
import { setExternalLogin, setLoading } from "@redux";
import { useNotify } from "@utils";
import { enumExternalMethod } from "@configs";

export const ModuleTelSignUp = () => {
    //page hooks
    const dispatch = useDispatch();
    const { error, success } = useNotify();
    const { t } = useTranslation();
    //page state
    const [errorPhone, setError] = useState<string>();

    //page variable
    const initialValues: IPhone = {
        phoneNumber: "",
    };

    const handleRegister = async (values: IPhone, { setSubmitting }: FormikHelpers<IPhone>) => {
        if (!values.phoneNumber) {
            setError(t("validation.isNumber"));
            return;
        }
        console.log(values.phoneNumber);
        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.registerPhone(values);
            if (res.data.message) {
                dispatch(setExternalLogin({ type: enumExternalMethod.TEL }));
                success(res.data.message);
            }
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };

    const { values, handleSubmit, isSubmitting, setFieldTouched, setFieldValue } = useFormik({
        initialValues: initialValues,
        onSubmit: handleRegister,
    });

    const handleChangePhoneNumber = (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string,
        extension: string
    ) => {
        if (!isValid) setError(t("validation.isNumber"));
        else {
            setError("");
        }
        setFieldValue("phoneNumber", fullNumber.replace(/(\s|-)/g, ""));
    };

    return (
        <ComponentSignUpTel
            values={values}
            handleSubmit={handleSubmit}
            isSubmitting={isSubmitting}
            setFieldTouched={setFieldTouched}
            setFieldValue={setFieldValue}
            handleChangePhoneNumber={handleChangePhoneNumber}
            errorPhone={errorPhone}
        />
    );
};
