import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useSelector } from "react-redux";
import { FormikErrors, FormikTouched } from "formik";
import { Col, Row, Select } from "antd";

import {
    IDate,
    IEmailForm,
    IHandleBlur,
    IHandleChange,
    IHandleChangeSelect,
    ISelect,
    ISurveyQuestions,
} from "@interfaces";
import {
    SharedButton,
    SharedCheckBox,
    SharedInput,
    SharedIntlTelInput,
    SharedSelect,
    StyledError,
} from "..";
import { selectAuth, selectMerchant } from "@redux";

interface IProps {
    days: ISelect[];
    onChangeDay: IHandleChangeSelect;
    onChangeMonth: IHandleChangeSelect;
    onChangeYear: IHandleChangeSelect;
    values: IEmailForm;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IEmailForm>;
    touched: FormikTouched<IEmailForm>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
    months: ISelect[];
    years: ISelect[];
    onChangeAdditionField: IHandleChangeSelect;
    date: IDate;
    surveyQuestions: ISurveyQuestions[];
    handleChangeAnswerByIndex: (answer: string | string[], index: number) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IEmailForm>>;
    setFieldValue: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IEmailForm>>;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
    errorPhone: string;
}

const { Option } = Select;

export const ComponentFromEmail = (props: IProps) => {
    const {
        days,
        months,
        years,
        onChangeDay,
        onChangeYear,
        onChangeMonth,
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        handleSubmit,
        isSubmitting,
        date,
        surveyQuestions,
        handleChangeAnswerByIndex,
        setFieldTouched,
        errorPhone,
        setFieldValue,
        setError,
    } = props;
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    const { userInfo } = useSelector(selectAuth);
    const contact = userInfo?.contact;

    const fields = merchant.signUpSettings?.fields;

    return (
        <StyledContainerEmailForm>
            <form onSubmit={handleSubmit}>
                <SharedInput
                    placeholder={t("page.auth.name")}
                    name="fullName"
                    value={values.fullName}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.fullName}
                    touched={touched.fullName}
                />
                <SharedIntlTelInput
                    setFieldTouched={setFieldTouched}
                    setFieldValue={setFieldValue}
                    value={values.phoneNumber || ""}
                    errorPhone={errorPhone}
                    setError={setError}
                    disabled={!!contact?.phoneNumber}
                />
                <StyledSharedInput
                    placeholder={t("page.auth._password")}
                    type="password"
                    name="password"
                    value={values.password}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.password}
                    touched={touched.password}
                />
                <SharedInput
                    placeholder={t("page.auth.confirm_password")}
                    type="password"
                    name="conformPassword"
                    value={values.conformPassword}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.conformPassword}
                    touched={touched.conformPassword}
                />
                <div className="day">
                    <div className="title">{t("page.auth.birth_date")}</div>
                    <Row gutter={16}>
                        <Col span={8}>
                            <SharedSelect value={date.day} data={days} onChange={onChangeDay} />
                        </Col>
                        <Col span={8}>
                            <SharedSelect
                                value={date.month}
                                data={months}
                                onChange={onChangeMonth}
                            />
                        </Col>
                        <Col span={8}>
                            <SharedSelect value={date.year} data={years} onChange={onChangeYear} />
                        </Col>
                    </Row>
                    {date.error && (
                        <StyledError visible={true}>{t("validation.is_required")} </StyledError>
                    )}
                </div>
                {surveyQuestions.length !== 0 &&
                    fields?.map((field, index) => {
                        return (
                            <>
                                <Select
                                    placeholder={field.fieldName}
                                    value={surveyQuestions[index].answers}
                                    onChange={(value: string[]) =>
                                        handleChangeAnswerByIndex(value, index)
                                    }
                                    key={index}
                                >
                                    {field.options.map((option, index) => {
                                        return (
                                            <Option key={index} value={option.optionName}>
                                                {option.optionName}
                                            </Option>
                                        );
                                    })}
                                </Select>
                            </>
                        );
                    })}
                <div className="bottom">
                    <SharedCheckBox
                        name="acceptTermsAndConditions"
                        value={values.acceptTermsAndConditions || false}
                        onChange={handleChange}
                        label={t("page.auth.accept_terms_conditions")}
                        errors={errors.acceptTermsAndConditions}
                        touched={touched.acceptTermsAndConditions}
                    />
                    <StyledButton
                        size="small"
                        bg_color={merchant.themeColor}
                        disabled={isSubmitting}
                        type="submit"
                    >
                        {t("page.auth.submit")}
                    </StyledButton>
                </div>
            </form>
        </StyledContainerEmailForm>
    );
};

const StyledContainerEmailForm = styled.div`
    padding: 11px;
    background-color: #fff;
    color: #676767;
    text-align: start;
    font-weight: normal;
    font-size: 20px;
    line-height: 27px;

    .title {
        margin-bottom: 8px;
    }
    .day {
        background-color: #f7f7f8;
        margin-bottom: 24px;
        padding: 11px;
        border-radius: 6px;
        .ant-select-selection {
            background-color: #fff;
        }
    }
    .bottom {
        width: fit-content;
        margin: 0 auto;
        margin-top: 18px;
        text-align: center;
        span {
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #676767;
        }
        .ant-checkbox-wrapper {
            display: block;
        }
    }

    .ant-select-selection {
        border-color: #d9d9d9 !important;
        box-shadow: none !important;
        height: 48px;
        font-size: 16px;
        color: black;
        display: flex;
        flex-direction: column;
        justify-content: center;
        background-color: #f7f7f8;
        margin-bottom: 24px;
        border: none;
        border-radius: 6px;
    }
    .selected-dial-code {
        padding-left: 0 !important;
    }
    .selected-flag {
        display: flex !important;
    }
    .intl-tel-input .country-list {
        width: 100%;
    }
    .intl-tel-input input {
        border-radius: 6px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;

const StyledSharedInput = styled(SharedInput)`
    margin-top: 24px;
`;
