import { useFormik, FormikHelpers } from "formik";
import * as Yup from "yup";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";

import { ComponentSignInEmail, ComponentSignUpEmail } from "@components";
import { IAuth, IEmail } from "@interfaces";
import { useYup } from "@validations";
import { authApi } from "@api";
import { setAuth, setExternalLogin, setLoading } from "@redux";
import { useNotify } from "@utils";
import { enumExternalMethod, PATH_SIGN_UP_EMAIL_FROM } from "@configs";

interface IProps {
    socials: JSX.Element[];
    handleForgotPassword: () => void;
}

export const ModuleEmailSignIn = (props: IProps) => {
    //page props
    const { socials, handleForgotPassword } = props;
    //page hooks
    const { YubEmailSignUp } = useYup();
    const dispatch = useDispatch();
    const { error } = useNotify();
    const history = useHistory();

    //page variable
    const initialValues: IEmail = {
        email: "",
        password: "",
    };
    const registerSchema = Yup.object().shape(YubEmailSignUp);

    const handleRegister = async (values: IEmail, { setSubmitting }: FormikHelpers<IEmail>) => {
        //
        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.registerEmail(values.email);
            const data = res.data as IAuth;
            dispatch(setAuth(data));
            dispatch(
                setExternalLogin({ type: enumExternalMethod.EMAIL_NOT_SOCIAL, isSignUp: true })
            );
            history.push(PATH_SIGN_UP_EMAIL_FROM);
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };
    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleRegister,
        });
    return (
        <>
            <ComponentSignInEmail
                handleForgotPassword={handleForgotPassword}
                socials={socials}
                handleBlur={handleBlur}
                handleChange={handleChange}
                errors={errors}
                touched={touched}
                values={values}
                handleSubmit={handleSubmit}
                isSubmitting={isSubmitting}
            />
        </>
    );
};
