import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { ReactElement } from "react";
import { Col, Row } from "antd";

import { ISelect } from "@interfaces";
import { SharedSelect } from "@components";

interface IProps {
    days: ISelect[];
    onChangeDay: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
    onChangeDay: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
    onChangeDay: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
    months: ISelect[];
    years: ISelect[];
}

export const ComponentDate = (props: IProps) => {
    const { days, months, years, onChangeDay } = props;
    //page hook
    const { t } = useTranslation();
    return (
        <StyledContainer>
            <div className="title">{t("page.sign_up.birth_date")}</div>
            <Row gutter={16}>
                <Col span={8}>
                    <SharedSelect data={days} onChange={onChangeDay} />
                </Col>
                <Col span={8}>
                    <SharedSelect data={months} onChange={onChangeDay} />
                </Col>
                <Col span={8}>
                    <SharedSelect data={years} onChange={onChangeDay} />
                </Col>
            </Row>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    padding: 11px;
    background-color: #f7f7f8;
    color: #676767;
    text-align: start;
    font-weight: normal;
    font-size: 20px;
    line-height: 27px;
    .title {
        margin-bottom: 8px;
    }
`;
