import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { Checkbox } from "antd";
import { FormikErrors, FormikTouched } from "formik";

import {
    StyledSignUp,
    ComponentSocialMedia,
    SharedSocialLine,
    SharedInput,
    SharedButton,
    IConEmail,
    IConPassword,
} from "..";
import { selectMerchant } from "@redux";
import { IEmail, IHandleBlur, IHandleChange } from "@interfaces";

interface IProps {
    socials: JSX.Element[];
    handleForgotPassword: () => void;
    values: IEmail;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IEmail>;
    touched: FormikTouched<IEmail>;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
}

export const ComponentSignInEmail = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const {
        socials,
        handleForgotPassword,
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        isSubmitting,
        handleSubmit,
    } = props;
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledContainer bg_color={merchant.themeColor}>
                <div className="title">
                    {t("page.auth.sign_in_to")} {merchant.name}
                </div>
                <div className="choose">{t("page.auth.choose_sign_in_method")}</div>
                <ComponentSocialMedia socials={socials} />
                <SharedSocialLine />
                <SharedInput
                    prefixIcon={<IConEmail />}
                    placeholder={t("page.auth.email")}
                    name="email"
                    value={values.email}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.email}
                    touched={touched.email}
                />
                <SharedInput
                    prefixIcon={<IConPassword />}
                    placeholder={t("page.auth.password")}
                    name="password"
                    value={values.password}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.password}
                    touched={touched.password}
                />
                <StyledButton size="large" bg_color={merchant.themeColor || ""}>
                    {t("page.auth.sign_in")}
                </StyledButton>
                <div className="forgot">
                    {t("page.auth.forgot")}
                    <span onClick={handleForgotPassword}>{t("page.auth.password")}</span>
                </div>
                <div className="remember">
                    <Checkbox>{t("page.auth.remember_me")}</Checkbox>
                </div>
            </StyledContainer>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div<{ bg_color: string }>`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .forgot {
        font-size: 16px;
        line-height: 21px;
        font-weight: bold;
        color: #646464;
        text-align: start;
        margin-top: 16px;
        span {
            color: ${(p) => p.bg_color};
            cursor: pointer;
        }
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        margin-top: 8px;
        color: #646464;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;
