import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { FormikErrors, FormikTouched } from "formik";

import { StyledSignUp, SharedSocialLine, SharedInput, SharedButton, IConEmail } from "..";
import { selectMerchant } from "@redux";
import { IEmail, IExternalAuthState, IHandleBlur, IHandleChange } from "@interfaces";
import { ModuleSocialMedia } from "@modules";
import { enumExternalAuthType, enumExternalMethod } from "@configs";

interface IProps {
    values: IEmail;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IEmail>;
    touched: FormikTouched<IEmail>;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
}

export const ComponentSignUpEmail = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { values, handleBlur, handleChange, errors, touched, isSubmitting, handleSubmit } = props;
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };

    const signUpMethods = merchant?.signUpSettings?.signUpMethods;

    const checkMethodSignUpSocial =
        signUpMethods?.includes(enumExternalMethod.GOOGLE) ||
        signUpMethods?.includes(enumExternalMethod.LINE) ||
        signUpMethods?.includes(enumExternalMethod.FACEBOOK);

    return (
        <StyledSignUp>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_up_to")} {merchant.name}
                    </div>
                    <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
                </StyledContainer>
                <ModuleSocialMedia state={state} />
                {(merchant?.signUpSettings?.signUpMethods?.length || 0) > 0 && <SharedSocialLine />}
                <SharedInput
                    prefixIcon={<IConEmail />}
                    placeholder={t("page.auth.email")}
                    name="email"
                    value={values.email}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.email}
                    touched={touched.email}
                />
                <StyledButton
                    disabled={isSubmitting}
                    size="large"
                    bg_color={merchant.themeColor}
                    type="submit"
                >
                    {t("page.auth.sign_up")}
                </StyledButton>
            </form>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
