import { enumValidation } from "@configs";
import { useTranslation } from "react-i18next";
import * as Yup from "yup";

export const YubEmailSignUp = () => {
    //page hook
    const { t } = useTranslation();
    return {
        email: Yup.string()
            .required(
                t("validation.required", {
                    name: t("page.auth.email"),
                })
            )
            .email(t("validation.email_invalid"))
            .max(
                enumValidation.MAX_LENGTH_INPUT,
                t("validation.max", {
                    name: t("page.auth.email"),
                    number: enumValidation.MAX_LENGTH_INPUT,
                })
            ),
    };
};

export const YubEmailSignUpForm = () => {
    //page hook
    const { t } = useTranslation();
    return {
        name: Yup.string()
            .required(
                t("validation.required", {
                    returnObjects: true,
                    name: convertTranslationCoupon("coupon_name"),
                })
            )
            .max(
                enumValidation.LENGTH_100,
                t("validation.max_length_number", {
                    returnObjects: true,
                    name: convertTranslationCoupon("coupon_name"),
                    number: enumValidation.LENGTH_100,
                })
            )
            .min(
                enumValidation.LENGTH_2,
                t("validation.min", {
                    returnObjects: true,
                    name: convertTranslationCoupon("coupon_name"),
                    number: enumValidation.LENGTH_2,
                })
            ),
        password: Yup.string().required(
            t("validation.required", {
                returnObjects: true,
                name: t("page.auth.new_password"),
            })
        ),
        conformPassword: Yup.string()
            .required(
                t("validation.required", {
                    returnObjects: true,
                    name: t("page.auth.new_confirm_password"),
                })
            )
            .oneOf([Yup.ref("password"), null], t("validation.password_must_match")),
    };
};
