import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { ReactElement } from "react";
import { Checkbox, Col, Row } from "antd";
import { useSelector } from "react-redux";

import { ISelect } from "@interfaces";
import { SharedButton, SharedInput, SharedSelect } from "@components";
import { selectMerchant } from "@redux";

interface IProps {
    days: ISelect[];
    onChangeDay: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
    onChangeMonth: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
    onChangeYear: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
    months: ISelect[];
    years: ISelect[];
    onChangeAdditionField: (
        value: string,
        option?:
            | ReactElement<any, string | React.JSXElementConstructor<any>>
            | ReactElement<any, string | React.JSXElementConstructor<any>>[]
    ) => void;
}

export const ComponentFromEmail = (props: IProps) => {
    const { days, months, years, onChangeDay, onChangeYear, onChangeMonth, onChangeAdditionField } =
        props;
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    return (
        <StyledContainer>
            <SharedInput placeholder={t("page.sign_up.email")} />
            <SharedInput placeholder={t("page.sign_up.tel")} />
            <SharedInput placeholder={t("page.sign_up.password")} />
            <SharedInput placeholder={t("page.sign_up.confirm_password")} />
            <div className="day">
                <div className="title">{t("page.sign_up.birth_date")}</div>
                <Row gutter={16}>
                    <Col span={8}>
                        <SharedSelect data={days} onChange={onChangeDay} />
                    </Col>
                    <Col span={8}>
                        <SharedSelect data={months} onChange={onChangeMonth} />
                    </Col>
                    <Col span={8}>
                        <SharedSelect data={years} onChange={onChangeYear} />
                    </Col>
                </Row>
            </div>
            <SharedSelect
                data={[]}
                onChange={onChangeAdditionField}
                placeholder="What fruits do you like"
            />
            <div className="bottom">
                <Checkbox>{t("page.sign_up.accept_terms_conditions")}</Checkbox>
                <StyledButton size="large" bg_color={merchant.themeColor}>
                    {t("page.sign_up.sign_up")}
                </StyledButton>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    padding: 11px;
    background-color: #fff;
    color: #676767;
    text-align: start;
    font-weight: normal;
    font-size: 20px;
    line-height: 27px;
    .title {
        margin-bottom: 8px;
    }
    .day {
        background-color: #f7f7f8;
        margin-bottom: 24px;
    }
    .bottom {
        text-align: center;
        margin-top: 42px;
        span {
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #676767;
        }
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
