import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";

import { ComponentHeaderAuth, ComponentSignUpEmail } from "@components";
import { ComponentFaceBookButton, ComponentLineButton } from "@components";
import { logout, selectMerchant } from "@redux";
import { enumExternalAuthType } from "@configs";
import { IExternalAuthState } from "@interfaces";
import { ModuleGoogle } from "@modules";
import { useNotify } from "@utils";

export const PageSignUpEmail = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { error } = useNotify();
    const history = useHistory();

    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };
    const socials: JSX.Element[] = [
        <ComponentFaceBookButton />,
        <ModuleGoogle errorHandler={errorHandler} state={state} />,
        <ComponentLineButton />,
    ];

    const errorHandler = (errors: any) => {
        //handle Error
        if (errors.response) {
            const { data, status } = errors.response;
            if (data?.message && status !== 500) {
                error(data?.message);
            } else {
                error(t("page.sign_up.sign_up_failed"));
            }
            dispatch(logout());
        }
    };

    return (
        <StyledContainer color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <ComponentSignUpEmail socials={socials} />
            <div className="sign_in">
                {t("page.sign_up.already_have_an_account")} <span>{t("page.sign_up.sign_in")}</span>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ color: string }>`
    .sign_in {
        position: relative;
        top: -85px;
        text-align: end;
        margin: 36px 36px 0 0;
        font-style: normal;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        span {
            color: ${(p) => p.color};
            cursor: pointer;
        }
    }
`;
