import styled from "styled-components";
import { useTranslation } from "react-i18next";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Navigation } from "swiper";
import "swiper/swiper-bundle.min.css";
import "swiper/swiper.min.css";
import "swiper/components/navigation/navigation.min.css";
import Parser from "html-react-parser";

import { BLANK_IMAGE_URL } from "@configs";
import { StyledPreview, SharedButton } from "@components";
import { IBenefit } from "@interfaces";
import { useSelector } from "react-redux";
import { selectMerchant } from "@redux";

interface IProps {
    data: IBenefit;
    handleClick: () => void;
}
SwiperCore.use([Navigation]);

export const ComponentBenefitDetail = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    //redux state
    const { themeColor } = useSelector(selectMerchant);

    // props
    const { data, handleClick } = props;

    return (
        <StyledPreview>
            <div className="page_body">
                <div className="img-field">
                    <Swiper slidesPerView={1}>
                        {data.imageUrl.length ? (
                            data.imageUrl.map((item, index) => (
                                <SwiperSlide key={index}>
                                    <div className="img-wrap">
                                        <img src={item} />
                                    </div>
                                </SwiperSlide>
                            ))
                        ) : (
                            <SwiperSlide>
                                <div className="img-wrap">
                                    <img className="empty-img" src={BLANK_IMAGE_URL} />
                                </div>
                            </SwiperSlide>
                        )}
                    </Swiper>
                </div>

                <div className="name-field">
                    <h5>{data.name}</h5>
                    <StyledMField>
                        {/* {filter()?.map((item) => (
                            <StyledMembership key={item._id} bgColor={"rgb(129 107 0)"}>
                                <Diamond size="26" />
                                <p>{item.membershipName}</p>
                            </StyledMembership>
                        ))} */}
                    </StyledMField>
                </div>
                <div className="description-field">
                    <h5>{t("page.reward.descriptions")}</h5>
                    <div>{Parser(data.description)}</div>
                </div>
            </div>
            <div className="btn-main">
                <StyledButton
                    size="large"
                    bg_color={themeColor}
                    type="submit"
                    onClick={handleClick}
                >
                    {t("page.benefit.collect_reward")}
                </StyledButton>
            </div>
        </StyledPreview>
    );
};

const StyledMField = styled.div`
    display: flex;
    flex-wrap: wrap;
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
