import { useSelector } from "react-redux";
import { useEffect } from "react";
import { useHistory } from "react-router";
import { useTranslation } from "react-i18next";

import { ComponentHeaderAuth, StyledContainerSocial, StyledSignUp } from "@components";
import { selectAuth } from "@redux";
import { ModuleEmail, ModuleSocial, ModuleTel } from "@modules";
import { enumExternalMethod, PATH_SIGN_UP } from "@configs";

export const PageSignUpFrom = () => {
    //page hooks
    const history = useHistory();
    const { t } = useTranslation();

    //redux states
    const { externalLogin } = useSelector(selectAuth);

    useEffect(() => {
        if (externalLogin) {
            history.push(PATH_SIGN_UP);
        }
    }, []);

    const render = () => {
        switch (externalLogin) {
            case enumExternalMethod.EMAIL:
                return <ModuleEmail />;
                break;
            case (enumExternalMethod.FACEBOOK, enumExternalMethod.GOOGLE, enumExternalMethod.LINE):
                return <ModuleSocial />;
                break;
            case enumExternalMethod.TEL:
                return <ModuleTel />;
                break;
            default:
                history.push(PATH_SIGN_UP);
                break;
        }
    };

    return (
        <ComponentHeaderAuth>
            <StyledSignUp>
                <StyledContainerSocial>
                    <div className="title">
                        {t("page.auth.fill_in_the_information_and_receive_special_benefits")}{" "}
                    </div>
                </StyledContainerSocial>
                {render()}
            </StyledSignUp>
        </ComponentHeaderAuth>
    );
};
