import styled from "styled-components";

export const StyledWelcome = styled.div<{ themeColor: string }>`
    text-align: center;
    font-family: "Segoe UI", "Roboto", sans-serif;
    .box_top {
        /* background: url("/images/bgWelcome.png"); */
        background: url("/images/bgWelcome.png");
        background-size: cover;
        padding-bottom: 26px;
    }
    .welcome {
        font-weight: bold;
        font-size: 25px;
        line-height: 33px;
        color: ${(p) => p.themeColor};
        padding-top: 26px;
    }
    .desc {
        width: 80%;
        margin: 16px auto 48px auto;
        font-size: 18px;
        line-height: 1.5rem;
        text-align: center;
        color: #000000;
    }
    .group_images {
        padding: 12px 0;
        img {
            width: 90%;
            margin-top: 12px;
            height: 380px;
            object-fit: cover;
        }
    }
`;
