import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useSelector } from "react-redux";

import { BLANK_IMAGE_URL } from "@configs";
import { IReward } from "@interfaces";
import { IconStarSmall } from "@components";
import { selectMerchant } from "@redux";

interface IProps {
    data: IReward;
    handleClick: (value: IReward) => void;
}

export const RewardItem = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    //page props
    const { data, handleClick } = props;
    //redux state
    const { themeColor, membershipTiers } = useSelector(selectMerchant);
    //page variable
    let nameMembershipTIersString = "";
    membershipTiers?.forEach((membershipTier, index) => {
        if (data.levelIds?.includes(membershipTier._id) && membershipTiers.length !== index + 1) {
            nameMembershipTIersString =
                nameMembershipTIersString + membershipTier.membershipName + ",";
        }
        if (data.levelIds?.includes(membershipTier._id) && membershipTiers.length === index + 1) {
            nameMembershipTIersString = nameMembershipTIersString + membershipTier.membershipName;
        }
    });
    nameMembershipTIersString = nameMembershipTIersString.slice(0, -1);

    return (
        <>
            <StyledContainer slot="container-end">
                <div className="image">
                    <div className="start">
                        <IconStarSmall color={themeColor} />
                        <span>{data.point}</span>
                    </div>
                    <img
                        src={data.imageUrl[0] || BLANK_IMAGE_URL}
                        onClick={() => handleClick(data)}
                    />
                    {nameMembershipTIersString ? (
                        <div className="silver_member">
                            <div className="line"></div>
                            <div className="text">
                                {nameMembershipTIersString}
                                {t("page.reward.MEMBER")}
                            </div>

                            <div className="line"></div>
                        </div>
                    ) : (
                        <></>
                    )}
                </div>
                <div className="desc" onClick={() => handleClick(data)}>
                    {data.name}
                </div>
            </StyledContainer>
        </>
    );
};

const StyledContainer = styled.div`
    width: 209px;
    background-color: #fff;
    margin: 0 24px 28px 0;
    border-radius: 6px;
    position: relative;
    overflow: hidden;
    box-shadow: rgb(231 230 230) 0 0 14px 2px;

    .starSmall {
        position: absolute;
        top: 12px;
        left: 12px;
        padding: 4px 6px;
        background-color: #fff;
        z-index: 10;
        border-radius: 16px;
        span {
            display: inline-block;
            margin: 0 6px;
            color: #000000;
            font-family: Segoe UI;
            font-style: normal;
            font-weight: 600;
            font-size: 14px;
        }
    }
    .image {
        width: 100%;
        height: 209px;
        position: relative;
        .start {
            position: absolute;
            display: flex;
            align-items: center;
            top: 12px;
            left: 12px;
            padding: 2px 6px;
            background-color: #fff;
            border-radius: 16px;
            z-index: 10;
            span {
                display: inline-block;
                margin-left: 4px;
                font-family: "Segoe UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: 600;
                font-size: 14px;
                color: #000000;
                top: 1px;
                position: relative;
            }
        }
        img {
            width: 100%;
            object-fit: cover;
            height: 100%;
            cursor: pointer;
        }
        .silver_member {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(0deg, rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3));
            display: flex;
            justify-content: center;
            align-items: center;
            padding: 0 10px;
            font-family: "Segoe UI", "Roboto", sans-serif;

            cursor: none;
            .line {
                width: 23px;
                height: 1px;
                background-color: #fff;
            }
            .text {
                margin-left: 12px;
                flex: 1;
                color: #fff;
                text-transform: uppercase;
                max-width: 70%;
                word-break: break-all;
                font-family: "Segoe UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: normal;
                font-size: 12px;
            }
        }
    }
    .desc {
        font-family: "Leelawadee UI", "Roboto", sans-serif;
        font-size: 15px;
        line-height: 1.5rem;
        margin-top: 8px;
        color: #000000;
        display: -webkit-box;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        padding: 8px 12px 18px 12px;
        cursor: pointer;
        font-style: normal;
        font-weight: normal;
    }
`;
