import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";

import { selectAuth, selectMerchant, setDetailUser } from "@redux";
import { PageUserDetail } from "@pages";

interface IProp {
    children?: JSX.Element;
}

export const ComponentHeaderAuth = (props: IProp) => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    //page variable
    const merchant = useSelector(selectMerchant);
    const { detailUser } = useSelector(selectAuth);
    const { children } = props;

    // const handleDetail = () => {
    //     dispatch(setDetailUser());
    // };

    return (
        <>
            {!detailUser ? (
                <>
                    <StyledContainerHeaderAuth
                        bg_color={merchant.themeColor}
                        sub_color={merchant.subColor || ""}
                    >
                        <div className="image">
                            <img src={merchant.logoUrl} />
                        </div>
                        <div className="content">
                            <div className="name">{merchant.name}</div>
                            <StyledButton onClick={handleDetail}>
                                {t("page.auth.membership_details")}&gt;
                            </StyledButton>
                        </div>
                        <div className="background--sub"></div>
                    </StyledContainerHeaderAuth>
                    {children}
                </>
            ) : (
                <PageUserDetail />
            )}
        </>
    );
};

const StyledContainerHeaderAuth = styled.div<{ bg_color: string; sub_color: string }>`
    width: 100%;
    height: 190px;
    background-color: ${(p) => p.bg_color};
    position: relative;
    overflow: hidden;
    position: relative;
    .image {
        border-radius: 50%;
        width: 365px;
        height: 365px;
        position: absolute;
        overflow: hidden;
        left: 55.35%;
        bottom: -65.83%;
        border: 10px solid ${(p) => p.sub_color};
        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }
    }
    .content {
        position: absolute;
        left: 6%;
        top: 16px;
        .name {
            font-family: "Segoe UI", "Roboto", sans-serif;
            font-style: normal;
            font-weight: bold;
            font-size: 18px;
            line-height: 24px;
            color: #ffffff;
            margin: 0 0 12px 8px;
        }
    }
    .background--sub {
        position: absolute;
        bottom: 0;
        left: 0;
        height: 88px;
        width: 100%;
        background: linear-gradient(
            180deg,
            rgba(255, 255, 255, 0) 19%,
            rgba(248, 248, 248, 0.502) 65.33%,
            #f8f8f8 100%
        );
    }
`;

const StyledButton = styled.div`
    padding: 4px 16px;
    background: rgba(0, 0, 0, 0.22);
    border-radius: 16px;
    color: #fff;
    font-style: normal;
    font-weight: normal;
    font-size: 13px;
    line-height: 17px;
    width: fit-content;
    cursor: pointer;
    font-family: "Leelawadee UI", "Roboto", sans-serif;
    display: flex;
    align-items: center;
`;
