import styled, { CSSProperties } from "styled-components";
import { useTranslation } from "react-i18next";
import { Avatar } from "antd";
import moment from "moment";
import { useSelector } from "react-redux";

import { ArrowRight, IConCamera, IConClose, IConTick, IConUser } from "@components";
import { IUserSetting } from "@interfaces";
import { enumUseSetting } from "@configs";
import { theme } from "@utils";
import { selectAuth } from "@redux";

const styleAvatarUser: CSSProperties = {
    transform: "scale(2)",
};

interface IProps {
    handleUploadImage: (e: React.ChangeEvent<HTMLInputElement>) => void;
    values: IUserSetting;
    handleChangeTitle: (title: enumUseSetting) => void;
    handleLinkLine: () => void;
}

export const ComponentUserSetting = (props: IProps) => {
    //page prop
    const { handleUploadImage, values, handleChangeTitle, handleLinkLine } = props;
    //page hooks
    const { t } = useTranslation();
    //redux page
    const { userInfo, isLoading } = useSelector(selectAuth);
    //page variable
    const dateFormat = moment(values?.dateOfBirth).format("DD/MM/YYYY");

    return (
        <>
            {isLoading && (
                <StyledUserSetting avatarCheck={!!values?.avatar}>
                    <input
                        type="file"
                        hidden
                        id="uploadImage"
                        onChange={handleUploadImage}
                        accept="image/png, image/jpeg, image/jpg"
                    />
                    <div className="image">
                        <div className="box_image">
                            {values?.avatar ? (
                                <Avatar alt="Avatar" size={64} src={values?.avatar} />
                            ) : (
                                <IConUser className="ant-avatar" style={styleAvatarUser} />
                            )}
                            <label className="camera" htmlFor="uploadImage">
                                <IConCamera />
                            </label>
                        </div>
                    </div>
                    <div className="content">
                        <StyledCartUser onClick={() => handleChangeTitle(enumUseSetting.FULL_NAME)}>
                            <div className="user_info">
                                <div className="title">{t("page.user_detail.name")}</div>
                                <div className="desc">{values?.fullName}</div>
                            </div>
                            <div className="arrowRight">
                                <ArrowRight />
                            </div>
                        </StyledCartUser>
                        <StyledCartUser
                            onClick={() => handleChangeTitle(enumUseSetting.PHONE_NUMBER)}
                        >
                            <div className="user_info">
                                <div className="title">{t("page.user_detail.phone_number")}</div>
                                <div className="desc">{values?.phoneNumber}</div>
                            </div>
                            <div className="arrowRight">
                                <ArrowRight />
                            </div>
                        </StyledCartUser>
                        <StyledCartUser onClick={() => handleChangeTitle(enumUseSetting.EMAIL)}>
                            <div className="user_info">
                                <div className="title">{t("page.user_detail.email")}</div>
                                <div className="desc">{values?.email}</div>
                            </div>
                            <div className="arrowRight">
                                {userInfo?.emailVerified ? (
                                    <IConTick color={theme.colors.green} />
                                ) : (
                                    <IConClose width={15} color="red" />
                                )}
                            </div>
                        </StyledCartUser>
                        <StyledCartUser
                            onClick={() => handleChangeTitle(enumUseSetting.DATE_OF_BIRTH)}
                        >
                            <div className="user_info">
                                <div className="title">{t("page.user_detail.date_of_birth")}</div>
                                <div className="desc">{dateFormat}</div>
                            </div>
                            <div className="arrowRight">
                                <ArrowRight />
                            </div>
                        </StyledCartUser>
                        <div className="line" onClick={handleLinkLine}>
                            <StyledCartUser>
                                <div className="user_info">
                                    <div className="desc">
                                        {t("page.user_detail.link_different")}
                                    </div>
                                </div>
                                <div className="arrowRight">
                                    <ArrowRight />
                                </div>
                            </StyledCartUser>
                        </div>
                        <div className="line_desc">{t("page.user_detail.request_link")}</div>
                    </div>
                </StyledUserSetting>
            )}
        </>
    );
};

const StyledUserSetting = styled.div<{ avatarCheck: boolean }>`
    .image {
        width: 100%;
        padding: 46px 0;
        background: #f7f7f8;
        /* background: #000; */
        text-align: center;
        img {
            border-radius: 50%;
        }
    }
    .box_image {
        position: relative;
        width: fit-content;
        margin: 0 auto;
    }
    .camera {
        position: absolute;
        bottom: 0;
        right: 0;
        cursor: pointer;
        ${(p) => {
            if (!p.avatarCheck)
                return `bottom: -15px;
                right: -15px;`;
        }}
    }
    .line {
        margin-top: 42px;
    }
    .line_desc {
        margin: 16px 12px;
        font-family: Segoe UI;
        font-style: normal;
        font-weight: normal;
        font-size: 14px;
        color: #707070;
    }
    .content {
        background: #f7f7f8;
        min-height: 100vh;
    }
`;

const StyledCartUser = styled.div`
    padding: 9px 16px;
    background-color: #fff;
    border-bottom: 1px solid #eeeeee;
    display: flex;
    justify-content: space-between;
    align-items: center;
    .arrowRight {
        cursor: pointer;
    }
    &:last-child {
        border-bottom: initial;
    }
    .user_info {
        .title {
            font-family: "Segoe UI", "Roboto", sans-serif;
            font-style: normal;
            font-weight: 600;
            font-size: 14px;
            color: #707070;
            margin-bottom: 4px;
        }
        .desc {
            font-family: "Leelawadee UI", "Roboto", sans-serif;
            font-style: normal;
            font-weight: bold;
            font-size: 18px;
            color: #000000;
        }
    }
`;
