import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router";
import { useTranslation } from "react-i18next";

import { benefitApi, couponApi, merchantApi } from "@api";
import { ComponentCouponDetail } from "@components";
import { PATH_404, PATH_SIGN_IN_EMAIL, enumStatus, enumTypes, PATH_HOME } from "@configs";
import { IBenefit, ICoupon, IMerchant, IMerchantQRcode } from "@interfaces";
import {
    selectAuth,
    setExternalTypes,
    setLoading,
    setMainColor,
    setMerchant,
    setMerchantID,
} from "@redux";
import { useNotify } from "@utils";

export const PageBenefit = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { id } = useParams<{ id: string }>();
    const history = useHistory();
    const { error, success } = useNotify();
    //page rdux
    const { auth } = useSelector(selectAuth);
    //page state
    const [benefit, setBenefit] = useState<IBenefit>();
    //page variable
    const token = auth?.accessToken;

    useEffect(() => {
        useCoupon();
    }, []);

    const useCoupon = async () => {
        dispatch(setLoading(true));
        const res = await benefitApi.getDetailBenefitById(id);
        console.log(res);
        const data = res.data as IBenefit;
        setBenefit(data);

        dispatch(setLoading(false));
    };

    const handleUseCoupon = async () => {
        try {
            dispatch(setLoading(true));
            const res = await couponApi.redeemedCoupon({
                objectId: coupon?._id || "",
                status: enumStatus.ACTIVE,
                type: enumTypes.COUPON,
            });
            if (res.data.message) {
                success(res.data.message);
                history.push(PATH_HOME);
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
        }
    };

    const handleRedeemedCoupon = async () => {
        try {
            dispatch(setLoading(true));
            const res = await couponApi.receiveCoupon({
                objectId: coupon?._id || "",
                status: enumStatus.ACTIVE,
                type: enumTypes.COUPON,
            });
            if (res.data.message) {
                success(res.data.message);
                history.push(PATH_HOME);
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
        }
    };

    return <></>;
};
