import styled from "styled-components";
import { useSelector } from "react-redux";
import { useEffect } from "react";
import { useHistory } from "react-router";
import { useTranslation } from "react-i18next";

import { ComponentHeaderAuth, StyledContainerSocial, StyledSignUp } from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { ModuleEmail } from "@modules";
import { enumExternalMethod, PATH_SIGN_UP } from "@configs";
import { PageUserDetail } from "@pages";

export const PageSignUpFrom = () => {
    //page hooks
    const history = useHistory();
    const { t } = useTranslation();

    //redux states
    const merchant = useSelector(selectMerchant);
    const { externalLogin, detailUser } = useSelector(selectAuth);

    useEffect(() => {
        if (externalLogin !== enumExternalMethod.EMAIL) {
            history.push(PATH_SIGN_UP);
        }
    }, []);

    return (
        <>
            {!detailUser ? (
                <StyledContainer color={merchant.themeColor}>
                    <StyledSignUp>
                        <ComponentHeaderAuth />
                        <StyledContainerSocial>
                            <div className="title">
                                {t(
                                    "page.auth.fill_in_the_information_and_receive_special_benefits"
                                )}{" "}
                            </div>
                        </StyledContainerSocial>
                        <ModuleEmail />
                    </StyledSignUp>
                </StyledContainer>
            ) : (
                <PageUserDetail />
            )}
        </>
    );
};

const StyledContainer = styled.div<{ color: string }>``;
