import { useTranslation } from "react-i18next";
import * as Yup from "yup";

import { enumValidation, MAX_LENGTH_NAME } from "@configs";

export const YubSUserSetting= () => {
    //page hook
    const { t } = useTranslation();
    return {
        email: Yup.string()
            .required(
                t("validation.required", {
                    name: t("page.auth.email"),
                })
            )
            .email(t("validation.email_invalid"))
            .max(
                enumValidation.MAX_LENGTH_INPUT,
                t("validation.max", {
                    name: t("page.auth.email"),
                    number: enumValidation.MAX_LENGTH_INPUT,
                })
            ),
        fullName: Yup.string()
            .required(
                t("validation.required", {
                    returnObjects: true,
                    name: t("page.auth.name"),
                })
            )
            .max(
                MAX_LENGTH_NAME,
                t("validation.max_length_number", {
                    returnObjects: true,
                    name: t("page.auth.name"),
                    number: MAX_LENGTH_NAME,
                })
            ),
    };
};
