import { FormikErrors, FormikTouched } from "formik";
import styled from "styled-components";
import { useTranslation } from "react-i18next";
import IntlTelInput, { CountryData } from "react-intl-tel-input";
import { DatePicker } from "antd";
import moment from "moment";

import { IHandleBlur, IUserSetting, IHandleChange } from "@interfaces";
import { SharedButton, SharedInput, StyledError } from "@components";
import { useSetting } from "@configs";

interface IProps {
    values: IUserSetting;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IUserSetting>;
    touched: FormikTouched<IUserSetting>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
    title: useSetting;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IUserSetting>>;
    handleChangePhoneNumber: (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string,
        extension: string
    ) => void;
    errorPhone: string;
    handleChangeDate: (date: moment.Moment, dateString: string) => void;
}

export const ComponentUserSettingSave = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //page props
    const {
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        handleSubmit,
        isSubmitting,
        title,
        setFieldTouched,
        handleChangePhoneNumber,
        errorPhone,
        handleChangeDate,
    } = props;

    const render = () => {
        switch (title) {
            case useSetting.FULL_NAME:
                return t("page.user_detail.name");
                break;
            case useSetting.PHONE_NUMBER:
                return t("page.user_detail.phone_number");
                break;
            case useSetting.EMAIL:
                return t("page.user_detail.email");
                break;
            case useSetting.DATE_OF_BIRTH:
                return t("page.user_detail.date");
                break;
            default:
                return <></>;
                break;
        }
    };

    const renderName = () => {
        switch (title) {
            case useSetting.FULL_NAME:
                return useSetting.FULL_NAME;
                break;
            case useSetting.PHONE_NUMBER:
                return useSetting.PHONE_NUMBER;
                break;
            case useSetting.EMAIL:
                return useSetting.EMAIL;
                break;
            default:
                return "";
                break;
        }
    };

    return (
        <StyledSettingSave>
            <form onSubmit={handleSubmit}>
                <div className="title">{render()}</div>
                {title !== useSetting.DATE_OF_BIRTH &&
                    (title === useSetting.PHONE_NUMBER ? (
                        <>
                            <IntlTelInput
                                defaultCountry="th"
                                preferredCountries={["th,vn"]}
                                fieldId="phoneNumber"
                                fieldName="phoneNumber"
                                onPhoneNumberChange={handleChangePhoneNumber}
                                onPhoneNumberBlur={() => {
                                    setFieldTouched(values[title].toString(), true);
                                }}
                                value={values[title]}
                                disabled={isSubmitting}
                            />
                            {errorPhone && <StyledError visible={true}>{errorPhone}</StyledError>}
                        </>
                    ) : (
                        <StyledSharedInput
                            name={renderName()}
                            value={values[title]}
                            onBlur={handleBlur}
                            onChange={handleChange}
                            errors={errors[title]}
                            touched={touched[title]}
                        />
                    ))}

                {title === useSetting.DATE_OF_BIRTH && (
                    <DatePicker
                        value={moment(values[title] || undefined)}
                        onChange={handleChangeDate}
                    />
                )}

                <StyledSharedButton
                    size="large"
                    bg_color="#05C755"
                    disabled={isSubmitting}
                    type="submit"
                >
                    {t("page.auth.sign_up")}
                </StyledSharedButton>
            </form>
        </StyledSettingSave>
    );
};

const StyledSharedInput = styled(SharedInput)`
    width: 90%;
    margin: 0 auto;
`;

const StyledSettingSave = styled.div`
    text-align: center;
    padding-top: 24px;
    .title {
        font-family: "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 14px;
        color: #707070;
        margin-bottom: 12px;
    }
    .allow-dropdown.intl-tel-input {
        width: 90%;
    }
    .intl-tel-input.allow-dropdown input[type="text"],
    .intl-tel-input.allow-dropdown input[type="tel"] {
        height: 48px !important;
        outline: none;
        border: none;
        background-color: #f7f7f8;
        width: 100% !important;
        font-size: 16px;
        color: black;
        box-shadow: none !important;
        font-weight: 500;
    }
    .ant-calendar-picker {
        width: 90%;
    }
    .ant-calendar-picker-input.ant-input {
        height: 48px;
        font-size: 16px;
    }
    .ant-input:focus {
        border-color: initial !important;
        box-shadow: initial;
    }
    .cBrgan {
        margin-left: 5%;
    }
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
    position: absolute;
    bottom: 48px;
    left: 5%;
`;
