import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import styled from "styled-components";
import QRCode from "react-qr-code";

import { IConRoom } from "@components";
import { selectAuth } from "@redux";

export const ComponentQRcode = () => {
    //page hook
    const { t } = useTranslation();
    //redux states
    const { userInfo } = useSelector(selectAuth);

    console.log(userInfo);
    return (
        <StyledContainer>
            {" "}
            <div className="room">
                <IConRoom />
            </div>
            <div className="content">
                <div className="my_qrCode">{t("page.qrCode.qrCode")}</div>
                <QRCode value={userInfo?._id || ""} size={200} />
                <div className="merchant_id">
                    <p className="title">{t("page.qrCode.member_id")}</p>
                    <p className="code">{userInfo?._id}</p>
                </div>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    width: 100%;
    position: fixed;
    top: 103px;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: #fff;
    padding: 70px;
    border-radius: 16px;
    z-index: 100;
    .content {
        width: 70%;
        margin: 0 auto;
        text-align: center;
    }
    .room {
        position: absolute;
        top: 24px;
        right: 24px;
    }
    .my_qrCode {
        font-weight: bold;
        font-size: 20px;
        line-height: 1.5rem;
        text-align: center;
        color: #000000;
        margin-bottom: 36px;
    }
    .merchant_id {
        margin-top: 36px;
        padding: 16px 24px;
        background: #f2f2f2;
        p {
            margin: none;
        }
        .code {
            color: #000000;
            font-weight: bold;
            font-size: 20px;
            margin-top: 4px;
            word-break: break-word;
        }
    }
`;
