import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { Col, Row, Select } from "antd";
import { useSelector } from "react-redux";
import { FormikErrors, FormikTouched } from "formik";

import {
    IDate,
    IHandleBlur,
    IHandleChange,
    IHandleChangeSelect,
    ISelect,
    ISocialMedia,
    ISurveyQuestions,
} from "@interfaces";
import {
    SharedButton,
    SharedCheckBox,
    SharedInput,
    SharedIntlTelInput,
    SharedSelect,
    StyledError,
} from "..";
import { selectMerchant } from "@redux";

interface IProps {
    days: ISelect[];
    onChangeDay: IHandleChangeSelect;
    onChangeMonth: IHandleChangeSelect;
    onChangeYear: IHandleChangeSelect;
    months: ISelect[];
    years: ISelect[];
    onChangeAdditionField: IHandleChangeSelect;
    values: ISocialMedia;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<ISocialMedia>;
    touched: FormikTouched<ISocialMedia>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
    date: IDate;
    surveyQuestions: ISurveyQuestions[];
    handleChangeAnswerByIndex: (answer: string | string[], index: number) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<ISocialMedia>>;
    errorPhone: string;
    setFieldValue: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<ISocialMedia>>;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
}

const { Option } = Select;

export const ComponentFromSocial = (props: IProps) => {
    const {
        days,
        months,
        years,
        onChangeDay,
        onChangeYear,
        onChangeMonth,
        // onChangeAdditionField,
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        handleSubmit,
        isSubmitting,
        date,
        surveyQuestions,
        handleChangeAnswerByIndex,
        setFieldTouched,
        errorPhone,
        setFieldValue,
        setError,
    } = props;
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    const fields = merchant.signUpSettings?.fields;

    return (
        <StyledContainer>
            <form onSubmit={handleSubmit}>
                <SharedInput
                    placeholder={t("page.auth.name")}
                    value={values.fullName}
                    name="fullName"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.fullName}
                    touched={touched.fullName}
                />
                <SharedIntlTelInput
                    setFieldTouched={setFieldTouched}
                    setFieldValue={setFieldValue}
                    value={values.phoneNumber || ""}
                    isSubmitting={isSubmitting}
                    errorPhone={errorPhone}
                    setError={setError}
                />
                <StyledSharedInput
                    placeholder={t("page.auth.email")}
                    value={values.email}
                    name="email"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.email}
                    touched={touched.email}
                />
                <div className="day">
                    <div className="title">{t("page.auth.birth_date")}</div>
                    <Row gutter={16}>
                        <Col span={8}>
                            <SharedSelect value={date.day} data={days} onChange={onChangeDay} />
                        </Col>
                        <Col span={8}>
                            <SharedSelect
                                value={date.month}
                                data={months}
                                onChange={onChangeMonth}
                            />
                        </Col>
                        <Col span={8}>
                            <SharedSelect value={date.year} data={years} onChange={onChangeYear} />
                        </Col>
                    </Row>
                    {date.error && (
                        <StyledError visible={true}>{t("validation.is_required")} </StyledError>
                    )}
                </div>
                {surveyQuestions.length !== 0 &&
                    fields?.map((field, index) => {
                        return (
                            <>
                                <Select
                                    placeholder={field.fieldName}
                                    value={surveyQuestions[index].answers}
                                    onChange={(value: string[]) =>
                                        handleChangeAnswerByIndex(value, index)
                                    }
                                >
                                    {field.options.map((option) => {
                                        return (
                                            <Option
                                                key={option.optionName}
                                                value={option.optionName}
                                            >
                                                {option.optionName}
                                            </Option>
                                        );
                                    })}
                                </Select>
                            </>
                        );
                    })}
                <div className="bottom">
                    <SharedCheckBox
                        name="acceptTermsAndConditions"
                        value={values.acceptTermsAndConditions || false}
                        onChange={handleChange}
                        label={t("page.auth.accept_terms_conditions")}
                        errors={errors.acceptTermsAndConditions}
                        touched={touched.acceptTermsAndConditions}
                    />
                    <StyledButton
                        size="small"
                        bg_color={merchant.themeColor}
                        disabled={isSubmitting}
                        type="submit"
                    >
                        {t("page.auth.sign_up")}
                    </StyledButton>
                </div>
            </form>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    padding: 11px;
    background-color: #fff;
    color: #676767;
    text-align: start;
    font-weight: normal;
    font-size: 20px;
    line-height: 27px;

    .title {
        margin-bottom: 8px;
    }
    .day {
        background-color: #f7f7f8;
        margin-bottom: 24px;
        padding: 11px;

        .ant-select-selection {
            background-color: #fff;
        }
    }
    .bottom {
        width: fit-content;
        margin: 0 auto;
        margin-top: 18px;
        text-align: center;
        span {
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #676767;
        }
        .ant-checkbox-wrapper {
            display: block;
        }
    }
    .ant-select-selection {
        border-color: #d9d9d9 !important;
        box-shadow: none !important;
        height: 48px;
        font-size: 16px;
        color: black;
        display: flex;
        flex-direction: column;
        justify-content: center;
        background-color: #f7f7f8;
        margin-bottom: 24px;
    }
    .selected-dial-code {
        padding-left: 0 !important;
    }
    .selected-flag {
        display: flex !important;
    }
    .intl-tel-input .country-list {
        width: 100%;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;

const StyledSharedInput = styled(SharedInput)`
    margin-top: 24px;
`;
