import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { PhoneOutlined } from "@ant-design/icons";
import { Checkbox } from "antd";

import {
    StyledSignUp,
    ComponentSocialMedia,
    SharedSocialLine,
    SharedInput,
    SharedButton,
} from "@components";
import { selectMerchant } from "@redux";

interface IProps {
    socials: JSX.Element[];
}

export const ComponentSignInTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { socials } = props;
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.sign_up.sign_up_to")} {merchant.name}
                </div>
                <div className="choose">{t("page.sign_up.choose_sign_up_method")}</div>
                <ComponentSocialMedia socials={socials} />
                <SharedSocialLine />
                <SharedInput
                    prefixIcon={<PhoneOutlined className="site-form-item-icon" />}
                    placeholder={t("page.sign_up.tel")}
                />
                <StyledButton size="large" bg_color={merchant.themeColor}>
                    {t("page.sign_up.sign_up")}
                </StyledButton>
                <div className="remember">
                    <Checkbox>{t("page.sign_up.remember_me")}</Checkbox>
                </div>
            </StyledContainer>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        margin-top: 8px;
        color: #646464;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
