import { YubEmailSignUp, YubEmailSignUpForm } from "./Email";
import { YubPhoneSignUp } from "./Phone";

export const useYup = () => {
    return {
        YubEmailSignUp: YubEmailSignUp(),
        YubEmailSignUpForm: YubEmailSignUpForm(),
        YubPhoneSignUp: YubPhoneSignUp(),
    };
};
