import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { FormikErrors } from "formik";
import IntlTelInput, { CountryData } from "react-intl-tel-input";
import "react-intl-tel-input/dist/main.css";

import { StyledSignUp, SharedSocialLine, SharedButton, StyledError } from "@components";
import { selectMerchant } from "@redux";
import { IPhone, IExternalAuthState } from "@interfaces";
import { enumExternalAuthType, enumExternalMethod } from "@configs";
import { ModuleSocialMedia } from "@modules";
interface IProps {
    values: IPhone;
    errorPhone: string | undefined;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
}

export const ComponentSignUpTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const {
        values,
        isSubmitting,
        handleSubmit,
        setFieldTouched,
        setFieldValue,
        errorPhone,
        setError,
    } = props;
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };
    const signUpMethods = merchant?.signUpSettings?.signUpMethods;
    const checkMethodSignUpSocial =
        signUpMethods?.includes(enumExternalMethod.GOOGLE) ||
        signUpMethods?.includes(enumExternalMethod.LINE) ||
        signUpMethods?.includes(enumExternalMethod.FACEBOOK);

    return (
        <StyledSignUp>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_up_to")} {merchant.name}
                    </div>
                    {checkMethodSignUpSocial && (
                        <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
                    )}
                </StyledContainer>
                {checkMethodSignUpSocial && (
                    <>
                        {" "}
                        <ModuleSocialMedia state={state} />
                        <SharedSocialLine />
                    </>
                )}
                <SharedIntlTelInput
                    setFieldTouched={setFieldTouched}
                    setFieldValue={setFieldValue}
                    value={values.phoneNumber || ""}
                    isSubmitting={isSubmitting}
                    errorPhone={errorPhone}
                    setError={setError}
                />
                {errorPhone && <StyledError visible={true}>{errorPhone}</StyledError>}
                <StyledButton
                    size="large"
                    bg_color={merchant.themeColor}
                    disabled={isSubmitting}
                    type="submit"
                >
                    {t("page.auth.sign_up")}
                </StyledButton>
            </form>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .selected-dial-code {
        padding-left: 0 !important;
    }
    .selected-flag {
        display: flex !important;
    }
    .intl-tel-input .country-list {
        width: 100%;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
