import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { useDispatch } from "react-redux";
import { useTranslation } from "react-i18next";

import { IReward } from "@interfaces";
import { rewardApi } from "@api";
import { setLoading } from "@redux";
import { ComponentRewardDetail } from "@components";
import { enumStatus, enumTypes } from "@configs";
import { useNotify } from "@utils";

export const PageReward = () => {
    //page hooks
    const { id } = useParams<{ id: string }>();
    const dispatch = useDispatch();
    const { error } = useNotify();
    const { t } = useTranslation();

    //page state
    const [reward, setReward] = useState<IReward>();
    const [count, setCount] = useState<number>(1);

    useEffect(() => {
        (async () => {
            if (id) {
                dispatch(setLoading(true));
                const res = await rewardApi.getDetailById(id);
                const data = res.data as IReward;
                setReward(data);
                dispatch(setLoading(false));
            }
        })();
    }, []);
    const handleAddCount = () => {
        if (reward) {
            if (count < (reward.limit || 1)) {
                setCount(count + 1);
            }
        }
    };
    const handleMinusCount = () => {
        if (count > 1) {
            setCount(count - 1);
        }
    };

    const handleCalculatePoint = () => {
        if (reward) {
            if (reward.point) return reward.point * count;
        }
    };

    const handleUseCoupon = async () => {
        //
        if (reward) {
            const variants = reward.variants.map((item) => item.name);
            try {
                const res = await rewardApi.redeemed({
                    objectId: reward._id || "",
                    quantity: reward?.quantity,
                    status: enumStatus.ACTIVE,
                    type: enumTypes.REWARD,
                    variants,
                });
                if (res.data.message) {
                    success(res.data.message);
                }
            } catch (err: any) {
                const { message } = err.response.data;
                if (message) {
                    error(message);
                } else {
                    error(t("message.use_coupon.fail"));
                }
            }
        }
    };

    return (
        <>
            {reward && (
                <ComponentRewardDetail
                    handleAddCount={handleAddCount}
                    handleMinusCount={handleMinusCount}
                    handleCalculatePoint={handleCalculatePoint}
                    count={count}
                    data={reward}
                    handleUseCoupon={handleUseCoupon}
                />
            )}
        </>
    );
};
