import { FormikErrors, FormikTouched } from "formik";
import styled from "styled-components";
import { useTranslation } from "react-i18next";

import { IHandleBlur, IUserSetting, IHandleChange } from "@interfaces";
import { SharedButton, SharedInput } from "@components";
import { useSetting } from "@configs";

interface IProps {
    values: IUserSetting;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IUserSetting>;
    touched: FormikTouched<IUserSetting>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
    title: useSetting;
}

export const ComponentUserSettingSave = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //page props
    const { values, handleBlur, handleChange, errors, touched, handleSubmit, isSubmitting, title } =
        props;

    const render = () => {
        switch (title) {
            case useSetting.FULL_NAME:
                return t("page.user_detail.name");
                break;
            case useSetting.PHONE_NUMBER:
                return t("page.user_detail.phone_number");
                break;
            case useSetting.EMAIL:
                return t("page.user_detail.email");
                break;
            case useSetting.DATE_OF_BIRTH:
                return t("page.user_detail.date");
                break;
            default:
                return <></>;
                break;
        }
    };

    const renderName = () => {
        switch (title) {
            case useSetting.FULL_NAME:
                return useSetting.FULL_NAME;
                break;
            case useSetting.PHONE_NUMBER:
                return useSetting.PHONE_NUMBER;
                break;
            case useSetting.EMAIL:
                return useSetting.EMAIL;
                break;
            default:
                return <></>;
                break;
        }
    };

    return (
        <StyledSettingSave>
            <form onSubmit={handleSubmit}>
                <div className="title">{render()}</div>
                {title !== useSetting.DATE_OF_BIRTH && (
                    <StyledSharedInput
                        name={renderName()}
                        value={values[title]}
                        onBlur={handleBlur}
                        onChange={handleChange}
                        errors={errors[title]}
                        touched={touched[title]}
                    />
                )}
                <StyledSharedButton
                    size="large"
                    bg_color="#05C755"
                    disabled={isSubmitting}
                    type="submit"
                >
                    {t("page.auth.sign_up")}
                </StyledSharedButton>
            </form>
        </StyledSettingSave>
    );
};

const StyledSharedInput = styled(SharedInput)`
    width: 90%;
    margin: 0 auto;
`;

const StyledSettingSave = styled.div`
    text-align: center;
    padding-top: 24px;
    .title {
        font-family: "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 14px;
        color: #707070;
        margin-bottom: 12px;
    }
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
`;
