import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import moment from "moment";
import Parser from "html-react-parser";

import { selectMerchant } from "@redux";
import { ICoupon } from "@interfaces";
import { enumUnit } from "@configs";

interface IProps {
    data: ICoupon;
}

export const CouponItem = (props: IProps) => {
    //page props
    const { data } = props;
    //page hook
    const { t } = useTranslation();
    //redux page
    const { themeColor } = useSelector(selectMerchant);

    const dateFormat = moment(data?.endDate).format("DD/MM/yyyy");

    return (
        <>
            <StyledContainerTop themeColor={themeColor}>
                <div className="right">
                    <div className="name">{data.name}</div>
                    <div className="desc">{Parser(data.guideline || "")}</div>
                </div>
                <div className="left">
                    <div className="money">
                        {data.benefit} {data.unit === enumUnit.PERCENTAGE ? "%" : "฿"}
                    </div>
                </div>
            </StyledContainerTop>
            <StyledContainerBottom themeColor={themeColor}>
                <div className="date">
                    {t("page.coupon.expiration_date")}: {dateFormat}
                </div>
                <div className="recceive_coupon">{t("page.coupon.recceive_coupon")}&gt;</div>
            </StyledContainerBottom>
        </>
    );
};

const StyledContainerTop = styled.div<{ themeColor: string }>`
    background-color: #fff;
    padding: 18px 24px 32px 24px;
    display: flex;
    justify-content: space-between;
    position: relative;
    .right {
        .name {
            font-weight: bold;
            font-size: 20px;
            line-height: 27px;
            color: #000000;
        }
        .desc {
            font-weight: normal;
            font-size: 12px;
            line-height: 16px;
            color: #8a8a8a;
            margin-top: 4px;
        }
    }
    .left {
        .money {
            font-weight: bold;
            font-size: 30px;
            line-height: 40px;
            color: ${(p) => p.themeColor};
        }
    }
    &::after {
        content: "";
        position: absolute;
        bottom: 0;
        left: 0;
        width: 30px;
        height: 30px;
        border-radius: 50%;
        background-color: #f7f7f8;
        transform: translate(-50%, 50%);
    }
    &::before {
        content: "";
        position: absolute;
        bottom: 0;
        right: 0;
        width: 30px;
        height: 30px;
        border-radius: 50%;
        background-color: #f7f7f8;
        transform: translate(50%, 50%);
    }
`;

const StyledContainerBottom = styled.div<{ themeColor: string }>`
    background-color: #fff;
    display: flex;
    justify-content: space-between;
    padding: 14px 24px 18px 24px;
    .date {
        font-weight: normal;
        font-size: 12px;
        line-height: 16px;
        color: #8a8a8a;
    }
    .recceive_coupon {
        font-style: normal;
        font-weight: bold;
        font-size: 13px;
        color: ${(p) => p.themeColor};
        cursor: pointer;
    }
`;
