import { StyledSignUp, ComponentSocialMedia } from "@components";
import { selectMerchant } from "@redux";
import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

interface IProps {
    socials: JSX.Element[];
}

export const ComponentSignUpEmail = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { socials } = props;
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.sign_up.sign_up_to")} {merchant.name}
                </div>
                <div className="choose">
                    {t("page.sign_up.choose_sign_up_method")}
                </div>
            </StyledContainer>
            <ComponentSocialMedia socials={socials} />
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
line-height: 21px;color: #676767;
    }
`;
