import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import styled from "styled-components";
import { useCallback, useEffect, useState } from "react";
import { Checkbox } from "antd";

import {
    ComponentGmailButton,
    ComponentHeaderAuth,
    ComponentPhoneButton,
    ComponentSocialMedia,
    SharedSocialLine,
    StyledContainerSignIn,
    StyledSignUp,
} from "@components";
import { logout, selectAuth, selectMerchant } from "@redux";
import {
    ModuleEmailSignIn,
    ModuleFaceBook,
    ModuleGoogle,
    ModuleLine,
    ModuleTelSignIn,
} from "@modules";
import {
    enumExternalMethod,
    PATH_RESET_PASSWORD_EMAIL,
    enumExternalAuthType,
    PATH_SIGN_UP,
} from "@configs";
import { PageUserDetail } from "@pages";
import { IExternalAuthState } from "@interfaces";
import { useNotify } from "@utils";

export const PageSignIn = () => {
    //page hook
    const { t } = useTranslation();
    const history = useHistory();
    const { error } = useNotify();
    const dispatch = useDispatch();
    //redux states
    const merchant = useSelector(selectMerchant);
    const { detailUser } = useSelector(selectAuth);
    //page variable
    const state: IExternalAuthState = {
        type: enumExternalAuthType.LOGIN,
    };
    const errorHandler = (errors: any) => {
        //handle Error
        if (errors.response) {
            const { data, status } = errors.response;
            if (data?.message && status !== 500) {
                error(data?.message);
            } else {
                error(t("page.auth.sign_in_failed"));
            }
            dispatch(logout());
        }
    };
    //page states
    const [loginMethod, setLoginMethod] = useState<enumExternalMethod>(enumExternalMethod.EMAIL);
    const [signinMethods, setSigninMethods] = useState<JSX.Element[]>([]);
    const [remember, setRemember] = useState<boolean>(false);

    useEffect(() => {
        setSigninMethods([
            <ModuleLine state={state} />,
            <ModuleFaceBook errorHandler={errorHandler} state={state} />,
            <ModuleGoogle errorHandler={errorHandler} state={state} />,
            <ComponentPhoneButton onClick={handleSwitchPhone} />,
        ]);
    }, []);

    const handleSignUp = () => {
        history.push(PATH_SIGN_UP);
    };

    const handleForgotPassword = useCallback(() => {
        history.push(PATH_RESET_PASSWORD_EMAIL);
    }, []);

    const handleToggleRemember = useCallback(() => {
        setRemember(!remember);
    }, [remember]);

    const handleSwitchEmail = useCallback(() => {
        setLoginMethod(enumExternalMethod.EMAIL);
        setSigninMethods([
            <ModuleLine state={state} />,
            <ModuleFaceBook errorHandler={errorHandler} state={state} />,
            <ModuleGoogle errorHandler={errorHandler} state={state} />,
            <ComponentPhoneButton onClick={handleSwitchPhone} />,
        ]);
    }, [signinMethods]);

    const handleSwitchPhone = useCallback(() => {
        setLoginMethod(enumExternalMethod.TEL);
        setSigninMethods([
            <ModuleLine state={state} />,
            <ModuleFaceBook errorHandler={errorHandler} state={state} />,
            <ModuleGoogle errorHandler={errorHandler} state={state} />,
            <ComponentGmailButton onClick={handleSwitchEmail} />,
        ]);
    }, [signinMethods]);

    return (
        <StyledContainerSignIn color={merchant.themeColor}>
            <ComponentHeaderAuth>
                <>
                    <StyledSignUp>
                        <StyledContainer bg_color={merchant.themeColor}>
                            <div className="title">
                                {t("page.auth.sign_in_to")} <br /> {merchant.name}
                            </div>
                            <div className="choose">{t("page.auth.choose_sign_in_method")}</div>
                            <ComponentSocialMedia socials={signinMethods} />
                            <SharedSocialLine />
                            {loginMethod === enumExternalMethod.EMAIL ? (
                                <ModuleEmailSignIn remember={remember} />
                            ) : (
                                <ModuleTelSignIn remember={remember} />
                            )}

                            {/* <ModuleEmailSignIn /> */}
                            <div className="forgot">
                                {t("page.auth.forgot")}
                                <span onClick={handleForgotPassword}>
                                    {t("page.auth.password")}
                                </span>
                            </div>
                            <div className="remember">
                                <Checkbox value={remember} onClick={handleToggleRemember}>
                                    {t("page.auth.remember_me")}
                                </Checkbox>
                            </div>
                        </StyledContainer>
                    </StyledSignUp>
                    {/* <ModuleEmailSignIn handleForgotPassword={handleForgotPassword} /> */}
                    <div className="sign_in">
                        {t("page.auth.already_have_an_account")}
                        <span onClick={handleSignUp}>{t("page.auth.sign_up")}</span>
                    </div>
                </>
            </ComponentHeaderAuth>
        </StyledContainerSignIn>
    );
};

const StyledContainer = styled.div<{ bg_color: string }>`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 18px;
        line-height: 1.25em;
        color: #000000;
        margin-bottom: 25px;
    }
    .choose {
        font-size: 15px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 30px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .forgot {
        font-size: 15px;
        line-height: 21px;
        font-weight: bold;
        color: #646464;
        text-align: start;
        margin-top: 16px;
        span {
            color: ${(p) => p.bg_color};
            cursor: pointer;
        }
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 15px;
        line-height: 21px;
        margin-top: 8px;
        color: #646464;
    }
`;
