import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { FormikErrors, FormikTouched } from "formik";

import {
    StyledSignUp,
    ComponentSocialMedia,
    SharedSocialLine,
    SharedInput,
    SharedButton,
    IConPhone,
} from "@components";
import { selectMerchant } from "@redux";
import { IPhone, IHandleBlur, IHandleChange } from "@interfaces";

interface IProps {
    socials: JSX.Element[];
    values: IPhone;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IPhone>;
    touched: FormikTouched<IPhone>;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
}

export const ComponentSignUpTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const {
        socials,
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        isSubmitting,
        handleSubmit,
    } = props;
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.auth.sign_up_to")} {merchant.name}
                </div>
                <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
            </StyledContainer>
            <ComponentSocialMedia socials={socials} />
            <SharedSocialLine />
            <SharedInput prefixIcon={<IConPhone />} placeholder={t("page.auth.tel")} />
            <StyledButton size="large" bg_color={merchant.themeColor}>
                {t("page.auth.sign_up")}
            </StyledButton>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
