import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { StyledSignUp, SharedButton } from "@components";
import { selectMerchant } from "@redux";
import { ComponentFromEmail } from "./From";

export const ComponentSignUpEmailInfo = () => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.sign_up.fill_in_the_information_and_receive_special_benefits")}{" "}
                </div>
            </StyledContainer>
            {/* from */}
            <ComponentFromEmail />
            <StyledButton size="large" bg_color={merchant.themeColor}>
                {t("page.sign_up.sign_up")}
            </StyledButton>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        max-width: 60%;
        margin: 0 auto 46px auto;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
