import styled from "styled-components";
import InfiniteScroll from "react-infinite-scroll-component";
import { Spin } from "antd";

import { ICouponList } from "@interfaces";
import { CouponItem } from "./Item";

interface IProps {
    data: ICouponList[];
    handleReceive: (id: string) => void;
    handleLoadMore: () => void;
    loadingPagination: boolean;
}

export const ComponentCouponList = (props: IProps) => {
    const { data, handleReceive, handleLoadMore, loadingPagination } = props;

    return (
        <StyledContainer>
            <InfiniteScroll
                dataLength={data.length}
                next={handleLoadMore}
                hasMore={true}
                loader={<Spin size="large" spinning={loadingPagination} />}
            >
                {data.map((item, index) => {
                    return (
                        <CouponItem handleReceive={handleReceive} data={item.qrtable} key={index} />
                    );
                })}
            </InfiniteScroll>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 25px 0;
    position: relative;
`;
