import { useTranslation } from "react-i18next";
import styled from "styled-components";
import OtpInput from "react-otp-input";

import { theme } from "@utils";
import { SharedButton } from "@components";

export const ComponentUserVerify = () => {
    const { t } = useTranslation();

    return (
        <StyledUserVerify>
            <div className="title">{t("page.verify.phone_desc")}</div>
            <div className="enter_it">{t("page.verify.enter_it_here")}</div>
            <OtpInput
                numInputs={6}
                inputStyle={{
                    width: checkWidth,
                    height: "3rem",
                    margin: `1rem ${margin}`,
                    fontSize: "2rem",
                    borderRadius: "4px",
                    border: "none",
                    background: "#f8f8f8",
                }}
                containerStyle={{
                    justifyContent: "center",
                    flexWrap: "wrap",
                    margin: "5rem 0 5rem 0",
                }}
                isInputNum
            />
            <StyledSharedButton size="large" bg_color={theme.colors.green} type="submit">
                {t("page.auth.save")}
            </StyledSharedButton>
        </StyledUserVerify>
    );
};

const StyledUserVerify = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: calc(100vh - 100px);
    width: 100%;
    flex-direction: column;
    .title {
        font-family: "Leelawadee UI", "Roboto", sans-serif;
        font-style: normal;
        font-size: 18px;
        font-weight: 600;
        line-height: 24px;
    }
    .enter_it {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-weight: 600;
        font-size: 14px;
        line-height: 19px;
    }
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
    position: absolute;
    bottom: 48px;
    left: 5%;
`;
