/* eslint-disable no-case-declarations */
import styled from "styled-components";
import InfiniteScroll from "react-infinite-scroll-component";
import { Spin } from "antd";

import { IBenefit, IReward, ICoupon, IFreePoint } from "@interfaces";
import { enumTypes, exchangedType } from "@configs";
import { ComponentHistoryCoupon } from "./Coupon";
import { ComponentHistoryBenefit } from "./Benefit";
import { ComponentHistoryReward } from "./Reward";
import { ComponentHistoryFreePoint } from "./FreePoint";
// import { CouponItem } from "./Item";

interface IProps {
    data: (IBenefit | IReward | ICoupon | IFreePoint)[];
    loadingPagination: boolean;
    handleLoadMore: () => void;
}

export const ComponentHistoryList = (props: IProps) => {
    const { data, loadingPagination, handleLoadMore } = props;
    const render = (dataItem: IBenefit | IReward | ICoupon | IFreePoint, index: number) => {
        switch (dataItem.type) {
            case enumTypes.COUPON:
                return <ComponentHistoryCoupon data={dataItem as ICoupon} key={index} />;
            case enumTypes.BENEFIT:
                return <ComponentHistoryBenefit data={dataItem as IBenefit} key={index} />;
            case enumTypes.REWARD:
                const data = dataItem as IReward;
                const dataFreePoint: IFreePoint = {
                    updatedAtHistory: data.updatedAtHistory,
                    exchangedType: exchangedType.RECEIVED,
                    name: data.name,
                    point: (data.quantityPoint || 0) * (data?.point || 0),
                };
                return (
                    <>
                        {" "}
                        <ComponentHistoryReward data={data} key={index} />
                        <ComponentHistoryFreePoint data={dataFreePoint} key={index} />
                    </>
                );

            case enumTypes.FREE_POINT:
                return <ComponentHistoryFreePoint data={dataItem as IFreePoint} key={index} />;
            default:
                return <></>;
                break;
        }
    };
    return (
        <StyledContainerHistoryList>
            <InfiniteScroll
                dataLength={data.length}
                next={handleLoadMore}
                hasMore={true}
                loader={
                    loadingPagination && (
                        <div className="example">
                            <Spin size="large" spinning={loadingPagination} />{" "}
                        </div>
                    )
                }
            >
                {data.map((item, index) => {
                    return render(item, index);
                })}
            </InfiniteScroll>
        </StyledContainerHistoryList>
    );
};

const StyledContainerHistoryList = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 25px 0;
    position: relative;
    .example {
        margin: 20px 0;
        margin-bottom: 20px;
        padding: 30px 50px;
        text-align: center;
        border-radius: 4px;
    }
`;
