import { LayoutError } from "@layouts";
import { IRoute } from "@interfaces";
import {
    PageError404,
    PageSignUpEmail,
    PageSignUpTel,
    PageSignUpOTP,
    PageSignUpEmailIFrom,
    PageSignUpSocialForm,
    PageSignInEmail,
    PageSignInTel,
    PageResetPasswordEmail,
    PageResetPassword,
    PageLineLogin,
    PageSignUpTelFrom,
    PageTrafficSource,
    PageHome,
    PageCoupon,
    PageReward,
    PageBenefit,
    PageWelcome,
    PageSignUpSocial,
} from "@pages";

export const PATH_HOME = "/";
export const PATH_LOGIN = "/Login";
export const PATH_404 = "/*";

//sign-up
export const PATH_SIGN_UP_EMAIL = "/sign-up/email";
export const PATH_SIGN_UP_TEL = "/sign-up/tel";
export const PATH_SIGN_UP_OTP = "/sign-up/otp";
export const PATH_SIGN_UP_EMAIL_FROM = "/sign-up/email/from";
export const PATH_SIGN_UP_SOCIAL_FROM = "/sign-up/social/from";
export const PATH_SIGN_UP_SOCIAL = "/sign-up/social";
export const PATH_SIGN_UP_TEL_FROM = "/sign-up/tel/from";
//sign-in
export const PATH_SIGN_IN_EMAIL = "/sign-in/email";
export const PATH_SIGN_IN_TEL = "/sign-in/tel";

//reset password
export const PATH_RESET_PASSWORD_EMAIL = "/reset-password/email";
export const PATH_RESET_PASSWORD = "/reset-password";

//line
export const LINE_URL = "https://api.line.me/v2";
export const LINE_AUTH_URL = "https://api.line.me/oauth2/v2.1";
export const LINE_ACCESS_URL = "https://access.line.me/oauth2/v2.1";
export const LINE_SCOPE = "profile%20openid%20email";
export const PATH_LINE_LOGIN = "/line-login";
//faceBook
export const FACEBOOK_GRAPH_URL = "https://graph.facebook.com/v12.0/oauth";
//traffic source
export const TRAFFIC_SOURCE_QR_CODE = "/traffic-source/:id";
//coupon
export const COUPON = "/coupon";

//reward
export const REWARD = "/reward";
//benefit
export const PATH_BENEFIT = "/benefit";
//welcome
export const PATH_WELCOME = "/welcome";
//user
export const PATH_USER_SETTING = "/user/setting";

export const routes: Array<IRoute> = [
    //home
    { path: PATH_HOME, component: PageHome, exact: true },

    //sign-up
    { path: PATH_SIGN_UP_EMAIL, component: PageSignUpEmail, exact: true },
    { path: PATH_SIGN_UP_TEL, component: PageSignUpTel, exact: true },
    { path: PATH_SIGN_UP_OTP, component: PageSignUpOTP, exact: true },
    { path: PATH_SIGN_UP_EMAIL_FROM, component: PageSignUpEmailIFrom, exact: true },
    { path: PATH_SIGN_UP_SOCIAL_FROM, component: PageSignUpSocialForm, exact: true },
    //sign in
    { path: PATH_SIGN_IN_EMAIL, component: PageSignInEmail, exact: true },
    { path: PATH_SIGN_IN_TEL, component: PageSignInTel, exact: true },
    { path: PATH_LINE_LOGIN, component: PageLineLogin, exact: true },
    { path: PATH_SIGN_UP_TEL_FROM, component: PageSignUpTelFrom, exact: true },
    { path: PATH_SIGN_UP_SOCIAL, component: PageSignUpSocial, exact: true },

    //reset password
    { path: PATH_RESET_PASSWORD_EMAIL, component: PageResetPasswordEmail, exact: true },
    { path: PATH_RESET_PASSWORD + "/:resetPasswordId", component: PageResetPassword, exact: true },
    //traffic source
    { path: TRAFFIC_SOURCE_QR_CODE, component: PageTrafficSource, exact: true },
    //coupon source
    { path: COUPON + "/:id" + "/:idParent", component: PageCoupon, exact: true },
    { path: COUPON + "/:id", component: PageCoupon, exact: true },
    //Reward source
    { path: REWARD + "/:id", component: PageReward, exact: true },
    //Benefit source
    { path: PATH_BENEFIT + "/:id", component: PageBenefit, exact: true },
    //welcome
    { path: PATH_WELCOME, component: PageWelcome, exact: true },



    { component: PageError404, exact: false, layout: LayoutError },
];
export const normalRoutes = [
    PATH_HOME,
    PATH_SIGN_UP_EMAIL_FROM,
    PATH_SIGN_UP_TEL_FROM,
    PATH_SIGN_UP_SOCIAL_FROM,
    COUPON,
];

export const authRoutes = [
    PATH_SIGN_UP_EMAIL,
    PATH_SIGN_UP_TEL,
    PATH_SIGN_UP_OTP,
    PATH_SIGN_IN_EMAIL,
    PATH_SIGN_IN_TEL,
    PATH_RESET_PASSWORD_EMAIL,
    PATH_RESET_PASSWORD,
    PATH_LINE_LOGIN,
    PATH_WELCOME,
    PATH_SIGN_UP_SOCIAL,
];
