import Parser from "html-react-parser";
import styled from "styled-components";

import { IBenefit, IMembershipTier } from "@interfaces";
import { BLANK_IMAGE_URL } from "@configs";

interface IProps {
    data: IBenefit;
    handleClick: (id: string) => void;
    dataRanks: IMembershipTier[];
}

export const ComponentBenefitItem = (props: IProps) => {
    //page props
    const { data, handleClick, dataRanks } = props;

    return (
        <StyledContainerBenefit>
            <div className="image">
                <img
                    src={data.imageUrl[0] || BLANK_IMAGE_URL}
                    onClick={() => handleClick(data._id || "")}
                />
            </div>
            <div className="content">
                <div className="name" onClick={() => handleClick(data._id || "")}>
                    {data.name}
                </div>
                <div className="ranks">
                    {dataRanks.map((dataRank) => {
                        return (
                            <StyledRank key={dataRank._id} color={dataRank.color}>
                                <img src={dataRank.iconUrl} />
                                <div className="membershipName"> {dataRank.membershipName}</div>
                            </StyledRank>
                        );
                    })}
                </div>
                <div className="desc">{Parser(data.description)}</div>
            </div>
        </StyledContainerBenefit>
    );
};

const StyledContainerBenefit = styled.div`
    display: flex;
    margin-bottom: 28px;
    background: #fff;
    .image {
        width: 170px;
        text-align: center;
        @media (max-width: ${(p) => p.theme.breakPoints.breakMobile}) {
            width: 120px;
        }
        img {
            width: 170px;
            height: 100%;
            object-fit: contain;
            cursor: pointer;
            @media (max-width: ${(p) => p.theme.breakPoints.breakMobile}) {
                width: 120px;
            }
        }
    }
    .content {
        padding: 16px;
        background-color: #fff;
        flex: 1;
        width: 100px;
        /* max-width: 100%; */
        .name {
            font-style: normal;
            font-weight: bold;
            font-size: 16px;
            line-height: 1.5rem;
            color: #000000;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            cursor: pointer;
            font-family: "Leelawadee UI", "Roboto", sans-serif;
        }
        .desc {
            font-weight: normal;
            font-size: 14px;
            line-height: 1.5rem;
            color: #8a8a8a;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
            max-height: 4.5rem;
            margin-top: 12px;

            * {
                font-family: "Leelawadee UI", "Roboto", sans-serif;
                color: #8a8a8a;
                font-style: normal;
                font-weight: normal;
            }
        }
    }
    .ranks {
        display: -webkit-box;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
    }
`;

const StyledRank = styled.div<{ color: string }>`
    display: inline-block;
    padding: 4px 6px;
    background-color: ${(p) => p.color};
    border-radius: 16px;
    color: #fff;
    font-weight: bold;
    font-size: 8px;
    margin: 6px 12px 0 0;
    img {
        width: 12px;
        height: 12px;
        object-fit: contain;
    }
    .membershipName {
        display: inline-block;
        font-size: 8px;
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-weight: bold;
        color: #ffffff;
        margin-left: 6px;
    }
`;
