import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { FormikHelpers, useFormik } from "formik";
import * as Yup from "yup";
import { CountryData } from "react-intl-tel-input";
import { useTranslation } from "react-i18next";

import { authApi, merchantApi } from "@api";
import { ComponentUserSetting, ComponentUserSettingSave } from "@components";
import { selectAuth, setLoading } from "@redux";
import { useNotify } from "@utils";
import { IUserSetting } from "@interfaces";
import { useYup } from "@validations";
import { useSetting } from "@configs";

export const PageUserSetting = () => {
    //page hooks
    const dispatch = useDispatch();
    const { error, success } = useNotify();
    const { YubSUserSetting } = useYup();
    const { t } = useTranslation();
    //redux state
    const { userInfo } = useSelector(selectAuth);
    //page state
    const [title, setTitle] = useState<useSetting | "">("");
    const [errorPhone, setError] = useState<string>();

    //page variable

    const contact = userInfo?.contact;
    const initialValue: IUserSetting = {
        avatar: userInfo?.avatar,
        fullName: contact?.fullName || "",
        dateOfBirth: contact?.dateOfBirth || "",
        phoneNumber: contact?.phoneNumber || "",
        email: contact?.email || "",
    };
    const registerSchema = Yup.object().shape(YubSUserSetting);

    const handleUploadImage = async (e: React.ChangeEventHandler<HTMLInputElement> | undefined) => {
        dispatch(setLoading(true));
        //@ts-ignore
        const values = e.file;
        const formData = new FormData();
        formData.append("file", values);
        try {
            const res = await merchantApi.uploadImage(formData);
            setFieldValue("avatar", res.data);
        } catch (err: any) {
            const data = err.response.data;
            const mess = Array.isArray(data.errors) ? data.errors[0] : data.message;
            error(mess);
        } finally {
            dispatch(setLoading(false));
        }
    };

    const handleSaveSetting = async (
        values: IUserSetting,
        { setSubmitting }: FormikHelpers<IUserSetting>
    ) => {
        //
        if (!values.phoneNumber) {
            setError(t("validation.isNumber"));
            return;
        }

        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.updateProfile(values);
            if (res.data.message) {
                success(t("message.update.success"));
            } else {
                success(t("message.update.success"));
            }
        } catch (errors: any) {
            const errorsResponse = errors.response.data.errors;
            if (Array.isArray(errorsResponse) && errorsResponse.length) {
                error(errorsResponse[0]);
            } else {
                error(t("message.update.fail"));
            }
        } finally {
            setTitle("");
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };

    const handleChangeTitle = (title: useSetting) => {
        setTitle(title);
    };

    const {
        values,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
        errors,
        touched,
        setFieldValue,
        setFieldTouched,
    } = useFormik({
        initialValues: initialValue,
        validationSchema: registerSchema,
        onSubmit: handleSaveSetting,
    });

    const handleChangePhoneNumber = (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string
    ) => {
        if (!isValid) setError(t("validation.isNumber"));
        else {
            setError("");
        }
        setFieldValue("phoneNumber", fullNumber.replace(/(\s|-)/g, ""));
    };

    return (
        <>
            {title ? (
                <ComponentUserSettingSave
                    values={values}
                    handleChange={handleChange}
                    handleBlur={handleBlur}
                    handleSubmit={handleSubmit}
                    isSubmitting={isSubmitting}
                    errors={errors}
                    touched={touched}
                    title={title}
                    setFieldTouched={setFieldTouched}
                    handleChangePhoneNumber={handleChangePhoneNumber}
                    errorPhone={errorPhone || ""}
                />
            ) : (
                <ComponentUserSetting
                    handleUploadImage={handleUploadImage}
                    values={values}
                    handleChangeTitle={handleChangeTitle}
                />
            )}
        </>
    );
};
