import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";

import { ComponentHeaderAuth, ComponentSignUpEmail } from "@components";
import { logout, selectMerchant } from "@redux";
import { enumExternalAuthType, PATH_SIGN_IN_EMAIL } from "@configs";
import { IExternalAuthState } from "@interfaces";
import { ModuleFaceBook, ModuleGoogle, ModuleLine } from "@modules";
import { useNotify } from "@utils";

export const PageSignUpEmail = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { error } = useNotify();
    const history = useHistory();

    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };

    const errorHandler = (errors: any) => {
        //handle Error
        if (errors.response) {
            const { data, status } = errors.response;
            if (data?.message && status !== 500) {
                error(data?.message);
            } else {
                error(t("page.auth.sign_up_failed"));
            }
            dispatch(logout());
        }
    };

    const socials: JSX.Element[] = [
        <ModuleFaceBook errorHandler={errorHandler} state={state} />,
        <ModuleGoogle errorHandler={errorHandler} state={state} />,
        <ModuleLine state={state} />,
    ];

    const handleSignIn = () => {
        history.push(PATH_SIGN_IN_EMAIL);
    };

    return (
        <StyledContainer color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <ComponentSignUpEmail socials={socials} />
            <div className="sign_in">
                {t("page.auth.already_have_an_account")}{" "}
                <span onClick={handleSignIn}>{t("page.auth.sign_in")}</span>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ color: string }>`
    .sign_in {
        position: relative;
        top: -85px;
        text-align: end;
        margin: 36px 36px 0 0;
        font-style: normal;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        span {
            color: ${(p) => p.color};
            cursor: pointer;
        }
    }
`;
