import styled from "styled-components";
import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useFormik } from "formik";
import * as Yup from "yup";

import { StyledSignUp, ComponentFromTel } from "@components";
import { DAYS, MONTHS, YEARS } from "@configs";
import { IDate, IEmailForm, ISelect } from "@interfaces";
import { useYup } from "@validations";

export const ModuleTel = () => {
    //page hook
    const { t } = useTranslation();
    const { YubEmailTelUpForm } = useYup();
    //page state
    const [date, setDate] = useState<IDate>({
        day: "",
        month: "",
        year: "",
        error: "",
    });
    //page variable
    const daySelects: ISelect[] = DAYS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });
    const monthSelects: ISelect[] = MONTHS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });
    const yearSelects: ISelect[] = YEARS.map((item) => {
        return {
            id: item,
            label: item,
        };
    });

    const initialValues: IEmailForm = {
        fullName: "",
        dateOrBirth: "",
        acceptTermsAndConditions: false,
    };
    const registerSchema = Yup.object().shape(YubEmailTelUpForm);

    const handleChangeDay = (value: string) => {
        setDate({
            ...date,
            day: value,
            error: "",
        });
    };
    const handleChangeMonth = (value: string) => {
        setDate({
            ...date,
            month: value,
            error: "",
        });
    };
    const handleChangeYear = (value: string) => {
        setDate({
            ...date,
            year: value,
            error: "",
        });
    };

    const handleChangeAdditionField = (value: string) => {
        //
        value;
    };

    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleRegister,
        });

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.auth.fill_in_the_information_and_receive_special_benefits")}{" "}
                </div>
            </StyledContainer>
            <ComponentFromTel
                days={daySelects}
                months={monthSelects}
                years={yearSelects}
                onChangeDay={handleChangeDay}
                onChangeMonth={handleChangeMonth}
                onChangeYear={handleChangeYear}
                onChangeAdditionField={handleChangeAdditionField}
            />
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        max-width: 70%;
        margin: 0 auto 46px auto;
    }
`;
