import { useFormik, FormikHelpers } from "formik";
import * as Yup from "yup";
import { useDispatch } from "react-redux";
import { useTranslation } from "react-i18next";

import { ComponentSignInEmail } from "@components";
import { IAuth, ILogin } from "@interfaces";
import { useYup } from "@validations";
import { authApi } from "@api";
import { setAuth, setExternalLogin, setLoading } from "@redux";
import { useNotify } from "@utils";
import { enumExternalMethod } from "@configs";
import { useState } from "react";

interface IProps {
    handleForgotPassword: () => void;
}

export const ModuleEmailSignIn = (props: IProps) => {
    //page props
    const { handleForgotPassword } = props;
    //page hooks
    const { YubEmailSignIn } = useYup();
    const dispatch = useDispatch();
    const { error, success } = useNotify();
    const { t } = useTranslation();

    //page state
    const [remember, setRemember] = useState<boolean>(false);
    //page variable
    const initialValues: ILogin = {
        email: "",
        password: "",
    };
    const registerSchema = Yup.object().shape(YubEmailSignIn);

    const handleLogin = async (values: ILogin, { setSubmitting }: FormikHelpers<ILogin>) => {
        //
        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.login(values);
            const data = res.data as IAuth;
            if (remember) {
                dispatch(setAuth(data));
            } else {
                dispatch(setAuth({ ...data, refreshToken: null }));
            }
            dispatch(setExternalLogin({ type: enumExternalMethod.EMAIL_NOT_SOCIAL }));
            success(t("message.login.success"));
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };

    const handleToggleRemember = () => {
        setRemember(!remember);
    };

    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleLogin,
        });
    return (
        <>
            <ComponentSignInEmail
                handleForgotPassword={handleForgotPassword}
                handleBlur={handleBlur}
                handleChange={handleChange}
                errors={errors}
                touched={touched}
                values={values}
                handleSubmit={handleSubmit}
                isSubmitting={isSubmitting}
                handleToggleRemember={handleToggleRemember}
                remember={remember}
            />
        </>
    );
};
