import { useTranslation } from "react-i18next";
import styled from "styled-components";

export const CouponItem = () => {
    //page hook
    const { t } = useTranslation();
    return (
        <StyledContainerTop>
            <div className="right">
                <div className="name">{t("page.coupon.discount_coupon")}</div>
                <div className="desc">{t("page.coupon.desc")}</div>
            </div>
            <div className="left">
                <div className="money">200à¸¿ </div>
            </div>
        </StyledContainerTop>
    );
};

const StyledContainerTop = styled.div`
    background-color: #fff;
    padding: 18px 24px 32px 24px;
    display: flex;
    justify-content: space-between;
    position: relative;
    .right {
        .name {
            font-weight: bold;
            font-size: 20px;
            line-height: 27px;
            color: #000000;
        }
        .desc {
            font-weight: normal;
            font-size: 12px;
            line-height: 16px;
            color: #8a8a8a;
            margin-top: 4px;
        }
    }
    .left {
        .money {
            font-weight: bold;
            font-size: 30px;
            line-height: 40px;
            color: #970000;
        }
    }
    &::after {
        content: "";
        position: absolute;
        bottom: 0;
        left: 0;
        width: 30px;
        height: 30px;
        border-radius: 50%;
        background-color: #f7f7f8;
        transform: translate(-50%, 50%);
    }
    &::before {
        content: "";
        position: absolute;
        bottom: 0;
        right: 0;
        width: 30px;
        height: 30px;
        border-radius: 50%;
        background-color: #f7f7f8;
        transform: translate(50%, 50%);
    }
`;
