import styled from "styled-components";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Navigation } from "swiper";
import "swiper/swiper-bundle.min.css";
import "swiper/swiper.min.css";
import "swiper/components/navigation/navigation.min.css";
import { useTranslation } from "react-i18next";

import { RewardItem } from "./Item";
import { IDataReward } from "@interfaces";

SwiperCore.use([Navigation]);

interface IProps {
    data: IDataReward[];
}

export const ComponentRewardList = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    //page props
    const { data } = props;

    return (
        <StyledContainer>
            {data.map((item) => {
                return (
                    <>
                        {" "}
                        <div className="money">
                            {item.quantity}{" "}
                            {item.quantity > 1 ? t("page.reward.points") : t("page.reward.point")}
                        </div>
                        <Swiper slidesPerView={1.8}>
                            {item.reward.map((values) => {
                                return (
                                    <>
                                        <SwiperSlide>
                                            <RewardItem data={values} />
                                        </SwiperSlide>
                                    </>
                                );
                            })}
                        </Swiper>
                    </>
                );
            })}
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 25px 0;
    position: relative;
    .money {
        font-style: normal;
        font-weight: 600;
        font-size: 16px;
        line-height: 21px;
        color: #a7a7a7;
        margin-bottom: 12px;
    }
    .list-reward {
        display: flex;
    }
`;
