import Parser from "html-react-parser";
import styled from "styled-components";

import { IBenefit, IMembershipTier } from "@interfaces";
import { BLANK_IMAGE_URL } from "@configs";

interface IProps {
    data: IBenefit;
    handleClick: (id: string) => void;
    dataRanks: IMembershipTier[];
}

export const ComponentBenefitItem = (props: IProps) => {
    //page props
    const { data, handleClick, dataRanks } = props;

    return (
        <StyledContainerBenefit>
            <img src={data.imageUrl[0] || BLANK_IMAGE_URL} />
            <div className="content">
                <div className="name" onClick={() => handleClick(data._id || "")}>
                    {data.name}
                </div>
                <div className="ranks">
                    {dataRanks.map((dataRank) => {
                        return (
                            <StyledRank color={dataRank.color}>
                                <img src={dataRank.iconUrl} />
                                {dataRank.membershipName}
                            </StyledRank>
                        );
                    })}
                </div>
                <div className="desc">{Parser(data.description)}</div>
            </div>
        </StyledContainerBenefit>
    );
};

const StyledContainerBenefit = styled.div<{ themeColor: string }>`
    display: flex;
    margin-bottom: 28px;
    img {
        width: 170px;
        height: 170px;
        object-fit: cover;
    }
    .content {
        padding: 16px;
        background-color: #fff;
        flex: 1;
        width: 100px;
        /* max-width: 100%; */
        .name {
            font-weight: bold;
            font-size: 16px;
            line-height: 1.5rem;
            color: #000000;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            cursor: pointer;
        }
        .desc {
            font-weight: normal;
            font-size: 14px;
            line-height: 1.5rem;
            color: #8a8a8a;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
            max-height: 4.5rem;
            margin-top: 12px;
            p {
                margin: initial;
                word-break: break-word;
            }
        }
    }
    .ranks {
        display: -webkit-box;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
    }
`;

const StyledRank = styled.div<{ color: string }>`
    display: inline-block;
    padding: 4px 6px;
    background-color: ${(p) => p.color};
    border-radius: 16px;
    color: #fff;
    font-weight: bold;
    font-size: 8px;
    margin: 6px 12px 0 0;
    img {
        width: 12px;
        height: 12px;
        object-fit: contain;
        margin-right: 6px;
    }
`;
