import { useTranslation } from "react-i18next";
import styled from "styled-components";

import { theme } from "@utils";
import { SharedButton } from "@components";

export const ComponentUserVerify = () => {
    const { t } = useTranslation();

    return (
        <StyledSettingSave>
            <StyledSharedButton size="large" bg_color={theme.colors.green} type="submit">
                {t("page.auth.save")}
            </StyledSharedButton>
        </StyledSettingSave>
    );
};

const StyledSettingSave = styled.div`
    text-align: center;
    padding-top: 24px;
    .title {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 14px;
        color: #707070;
        margin-bottom: 12px;
    }
    .allow-dropdown.intl-tel-input {
        width: 90%;
    }
    .intl-tel-input.allow-dropdown input[type="text"],
    .intl-tel-input.allow-dropdown input[type="tel"] {
        height: 48px !important;
        outline: none;
        border: none;
        background-color: #f7f7f8;
        width: 100% !important;
        font-size: 16px;
        color: black;
        box-shadow: none !important;
        font-weight: 500;
    }
    .ant-calendar-picker {
        width: 90%;
    }
    .ant-calendar-picker-input.ant-input {
        height: 48px;
        font-size: 16px;
    }
    .ant-input:focus {
        border-color: initial !important;
        box-shadow: initial;
    }
    .custom-error {
        margin-left: 5%;
    }
    .selected-dial-code {
        padding-left: 0 !important;
    }
    .selected-flag {
        display: flex !important;
    }
    .intl-tel-input .country-list {
        width: 100%;
    }
    .intl-tel-input input {
        border-radius: 6px;
    }
    input {
        font-family: "Leelawadee UI", "Roboto", sans-serif;
    }
    .length {
        position: absolute;
        right: 5%;
        top: 24px;
        font-family: "Segoe UI", "Roboto", sans-serif;
    }
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
    position: absolute;
    bottom: 48px;
    left: 5%;
`;
