import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import styled from "styled-components";
import { useEffect, useState } from "react";

import {
    ComponentHeaderAuth,
    ComponentSocialMedia,
    SharedSocialLine,
    StyledContainerSignIn,
    StyledSignUp,
} from "@components";
import { logout, selectAuth, selectMerchant } from "@redux";
import {
    ModuleEmailSignUp,
    ModuleFaceBook,
    ModuleGoogle,
    ModuleLine,
    ModuleTelSignUp,
} from "@modules";
import { enumExternalMethod, enumExternalAuthType, PATH_SIGN_IN } from "@configs";
import { PageUserDetail } from "@pages";
import { IExternalAuthState } from "@interfaces";
import { useNotify } from "@utils";

export const PageSignUp = () => {
    //page hook
    const { t } = useTranslation();
    const history = useHistory();
    const { error } = useNotify();
    const dispatch = useDispatch();
    //redux states
    const merchant = useSelector(selectMerchant);
    const { detailUser } = useSelector(selectAuth);
    //page variable
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };
    const signUpMethods = merchant?.signUpSettings?.signUpMethods;
    const checkMethodSignUpSocial =
        signUpMethods?.includes(enumExternalMethod.GOOGLE) ||
        signUpMethods?.includes(enumExternalMethod.LINE) ||
        signUpMethods?.includes(enumExternalMethod.FACEBOOK);
    const checkMethodSignUpEmail = signUpMethods?.includes(enumExternalMethod.EMAIL);
    const checkMethodSignUpTel = signUpMethods?.includes(enumExternalMethod.TEL);

    const errorHandler = (errors: any) => {
        //handle Error
        if (errors.response) {
            const { data, status } = errors.response;
            if (data?.message && status !== 500) {
                error(data?.message);
            } else {
                error(t("page.auth.sign_in_failed"));
            }
            dispatch(logout());
        }
    };
    //page states
    const [signinMethods, setSigninMethods] = useState<JSX.Element[]>([]);

    useEffect(() => {
        if (signUpMethods?.includes(enumExternalMethod.LINE)) {
            signinMethods.push(<ModuleLine state={state} />);
        }
        if (signUpMethods?.includes(enumExternalMethod.FACEBOOK)) {
            signinMethods.push(<ModuleFaceBook errorHandler={errorHandler} state={state} />);
        }
        if (signUpMethods?.includes(enumExternalMethod.GOOGLE)) {
            signinMethods.push(<ModuleGoogle errorHandler={errorHandler} state={state} />);
        }
        setSigninMethods([...signinMethods]);
    }, []);

    const handleSignIn = () => {
        history.push(PATH_SIGN_IN);
    };

    return (
        <>
            {!detailUser ? (
                <StyledContainerSignIn color={merchant.themeColor}>
                    <ComponentHeaderAuth>
                        <StyledSignUp>
                            <StyledContainer bg_color={merchant.themeColor}>
                                <div className="title">
                                    {t("page.auth.sign_in_to")} <br /> {merchant.name}
                                </div>
                                {checkMethodSignUpSocial && (
                                    <div className="choose">
                                        {t("page.auth.choose_sign_up_method")}
                                    </div>
                                )}
                                <ComponentSocialMedia socials={signinMethods} />
                                {(checkMethodSignUpEmail || checkMethodSignUpTel) && (
                                    <SharedSocialLine />
                                )}
                                {checkMethodSignUpEmail ? (
                                    <ModuleEmailSignUp />
                                ) : checkMethodSignUpTel ? (
                                    <ModuleTelSignUp />
                                ) : (
                                    <></>
                                )}

                                {/* <ModuleEmailSignIn /> */}
                            </StyledContainer>
                        </StyledSignUp>
                        {/* <ModuleEmailSignIn handleForgotPassword={handleForgotPassword} /> */}
                        <div className="sign_in">
                            {t("page.auth.already_have_an_account")}
                            <span onClick={handleSignIn}>{t("page.auth.sign_in")}</span>
                        </div>
                    </ComponentHeaderAuth>
                </StyledContainerSignIn>
            ) : (
                <PageUserDetail />
            )}
        </>
    );
};

const StyledContainer = styled.div<{ bg_color: string }>`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 18px;
        line-height: 1.25em;
        color: #000000;
        margin-bottom: 25px;
    }
    .choose {
        font-size: 15px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 30px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .forgot {
        font-size: 15px;
        line-height: 21px;
        font-weight: bold;
        color: #646464;
        text-align: start;
        margin-top: 16px;
        span {
            color: ${(p) => p.bg_color};
            cursor: pointer;
        }
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 15px;
        line-height: 21px;
        margin-top: 8px;
        color: #646464;
    }
`;
