import queryString from "querystring";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { enumExternalMethod } from "@configs";
import { logout, selectAuth, setExternalLogin } from "@redux";
import { useHistory } from "react-router";

export const PageLineLogin = () => {
    //page hook
    const dispatch = useDispatch();
    const history = useHistory();
    //page variable
    const queryParams = queryString.parse(window.location.search.substring(1));

    useEffect(() => {
        if (queryParams.code) {
            if (queryParams.state) {
                dispatch(
                    setExternalLogin({
                        type: enumExternalMethod.LINE,
                        line: {
                            code: queryParams.code.toString(),
                            state: queryParams.state.toString(),
                        },
                    })
                );
            } else {
                dispatch(logout());
            }
        } else {
            dispatch(logout());
        }
    }, [queryParams]);

    return <></>;
};
